SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE criar_lote_auditoria ( cd_convenio_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, nr_seq_lote_audit_p INOUT bigint) AS $body$
DECLARE

			
			
nr_reg_atual_w			regra_num_reg_audit.nr_atual%type;
ie_adicional_w			regra_num_reg_audit.ie_adicional%type;
nr_seq_lote_audit_w		lote_auditoria.nr_sequencia%type;
nr_registro_w			varchar(255);

BEGIN

select	nextval('lote_auditoria_seq')
into STRICT	nr_seq_lote_audit_w
;

select	max(a.nr_atual) atual,
	max(a.ie_adicional) adicional
into STRICT	nr_reg_atual_w,
	ie_adicional_w
from	regra_num_reg_audit a
where	a.cd_estabelecimento	= cd_estabelecimento_p;

if (coalesce(ie_adicional_w,'N')	= 'N') then
	nr_registro_w	:= to_char(nr_reg_atual_w + 1);
elsif (ie_adicional_w			= 'A') then
	nr_registro_w	:= to_char(nr_reg_atual_w + 1) || to_char(clock_timestamp(),'yyyy');
end if;

insert 	into lote_auditoria(cd_convenio,
	cd_estabelecimento,
	dt_atualizacao,
	dt_lote,
	nm_usuario,
	nr_sequencia,
	nr_registro)
values (cd_convenio_p,
	cd_estabelecimento_p,
	clock_timestamp(),
	clock_timestamp(),
	nm_usuario_p,
	nr_seq_lote_audit_w,
	(nr_registro_w)::numeric );

update	regra_num_reg_audit
set	nr_atual		= nr_atual + 1
where	cd_estabelecimento	= cd_estabelecimento_p;

nr_seq_lote_audit_p := nr_seq_lote_audit_w;

/*CHAMA NA BAIXA_TITULO_CONVENIO DEIXAR SEM COMMIT*/

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
