CREATE OR REPLACE PROCEDURE criar_relatorio_1095 ( data_p text, cliente_p text, projeto_p text, cronograma_p bigint) AS $body$
DECLARE



nr_seq_rat_w	bigint;
nr_seq_etapa_w	bigint;
qt_min_ativ_w 	double precision;
dt_inicio_ativ_w	timestamp;
nr_seq_superior_w	bigint;
C01 CURSOR FOR
	SELECT	a.nr_sequencia,
		c.nr_sequencia,
		b.qt_min_ativ,
		b.dt_inicio_ativ
	from	proj_rat a,
		proj_rat_ativ b,
		proj_cron_etapa c,
		proj_cronograma d,
		proj_projeto e
	where	a.nr_sequencia = b.nr_seq_rat
	and	b.nr_seq_etapa_cron = c.nr_sequencia
	and	C.nr_seq_cronograma = d.nr_sequencia
	and	d.nr_seq_proj = e.nr_sequencia
	and	to_char(b.dt_inicio_ativ,'yyyy') = data_p
	and	e.nr_seq_cliente = CASE WHEN cliente_p='' THEN e.nr_seq_cliente  ELSE cliente_p END
	and	e.nr_sequencia =  CASE WHEN projeto_P='' THEN e.nr_sequencia  ELSE projeto_P END 
	and	d.nr_sequencia = CASE WHEN cronograma_p=0 THEN d.nr_sequencia  ELSE cronograma_p END;


BEGIN

delete from w_rat_relatorio_1095;
commit;


open C01;
loop
fetch C01 into
	nr_seq_rat_w,
	nr_seq_etapa_w,
	qt_min_ativ_w,
	dt_inicio_ativ_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

		nr_seq_superior_w := obter_etapa_ativ_sup_1(nr_seq_etapa_w,'S');

		if nr_seq_superior_w = 0 then
			nr_seq_superior_w := nr_seq_etapa_w;
		end if;

		insert into w_rat_relatorio_1095 values (nr_seq_rat_w, nr_seq_superior_w, qt_min_ativ_w, dt_inicio_ativ_w);
		commit;

	end;
end loop;
close C01;

commit;

end;
$body$
