CREATE OR REPLACE PROCEDURE criar_rev_idioma ( nr_sequencia_p bigint, nm_usuario_p text, cd_pessoa_revisao_p text ) AS $body$
DECLARE


cd_revisao_w		bigint;


BEGIN
	select	coalesce(max(somente_numero(cd_revisao)),0) + 1
	into STRICT	cd_revisao_w
	from	qua_doc_revisao
	where	nr_seq_doc = nr_sequencia_p;

	insert into qua_doc_revisao(
		nr_sequencia,
		nr_seq_doc,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		dt_revisao,
		cd_revisao,
		cd_pessoa_revisao)
	values (nextval('qua_doc_revisao_seq'),
		nr_sequencia_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		cd_revisao_w,
		cd_pessoa_revisao_p);

	CALL qua_atualizar_status_doc(nr_sequencia_p,'R',nm_usuario_p);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
