CREATE OR REPLACE PROCEDURE criar_seq_philips_template (nr_seq_template_p ehr_template.nr_sequencia%type, nm_usuario_p text) AS $body$
DECLARE

nr_seq_philips_w   ehr_template_philips.nr_seq_philips%type;

BEGIN
	select 	coalesce(max(nr_seq_philips), 0)+1
	into STRICT 	nr_seq_philips_w
	from 	ehr_template;
	
	update  ehr_template
	set 	nr_seq_philips = nr_seq_philips_w,
		dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p
	where nr_sequencia = nr_seq_template_p;
	
	commit;

end;
