CREATE OR REPLACE PROCEDURE cta_obter_regra_resp_pend (nr_seq_tipo_p bigint, nr_seq_estagio_p bigint, dt_pendencia_p timestamp, nr_seq_regra_resp_p INOUT bigint, nr_seq_regra_item_p INOUT bigint) AS $body$
DECLARE


nr_seq_resp_pend_w		bigint;
nr_seq_estagio_w		bigint;
nr_sequencia_w			bigint;

C01 CURSOR FOR
	SELECT	nr_seq_regra_pend
	from	cta_tipo_regra_resp
	where	nr_seq_tipo_pend = nr_seq_tipo_p
	and 	coalesce(dt_pendencia_p, clock_timestamp()) between dt_inicio_vigencia and coalesce(dt_fim_vigencia,clock_timestamp())
	and 	dt_inicio_vigencia = 	(SELECT max(dt_inicio_vigencia)
					from 	cta_tipo_regra_resp
					where	nr_seq_tipo_pend = nr_seq_tipo_p
					and 	coalesce(dt_pendencia_p, clock_timestamp()) between dt_inicio_vigencia and coalesce(dt_fim_vigencia,clock_timestamp()))
	order by nr_sequencia;

C02 CURSOR FOR
	SELECT	nr_sequencia,
		nr_seq_estagio
	from	cta_regra_resp_item
	where	nr_seq_regra = nr_seq_resp_pend_w
	order by coalesce(nr_seq_estagio,0);


BEGIN

nr_seq_regra_item_p:= 0;
nr_seq_regra_resp_p:= 0;

open C01;
loop
fetch C01 into
	nr_seq_resp_pend_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	open C02;
	loop
	fetch C02 into
		nr_sequencia_w,
		nr_seq_estagio_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin
		if (coalesce(nr_seq_estagio_w,nr_seq_estagio_p) = nr_seq_estagio_p) then
			nr_seq_regra_item_p:= nr_sequencia_w;
			nr_seq_regra_resp_p:= nr_seq_resp_pend_w;
		end if;
		end;
	end loop;
	close C02;

	end;
end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
