SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ctb_abrir_fechar_mes_estab ( nr_sequencia_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE



dt_fechamento_w			timestamp;
qt_referencia_w			bigint;
nr_seq_lote_w			bigint;
cd_estabelecimento_w		smallint;
ie_fecha_estab_w		varchar(1);
cd_empresa_w			smallint;
nm_estabelecimento_w		varchar(80);
nr_seq_log_w			bigint := 0;


BEGIN

select	ie_fecha_estab,
	a.cd_empresa
into STRICT	ie_fecha_estab_w,
	cd_empresa_w
from	empresa b,
	ctb_mes_ref a
where	a.nr_sequencia = nr_sequencia_p
and	a.cd_empresa = b.cd_empresa;

select	max(substr(obter_nome_estabelecimento(cd_estabelecimento_p),1,80))
into STRICT	nm_estabelecimento_w
;

if (ie_fecha_estab_w = 'S') then

	select	max(dt_fechamento)
	into STRICT	dt_fechamento_w
	from	ctb_mes_ref_estab
	where	nr_seq_mes		= nr_sequencia_p
	and	cd_estabelecimento 	= cd_estabelecimento_p;

	if (coalesce(dt_fechamento_w::text, '') = '') then
		begin

		select	count(*),
			max(nr_lote_contabil),
			max(cd_estabelecimento)
		into STRICT	qt_referencia_w,
			nr_seq_lote_w,
			cd_estabelecimento_w
		from	lote_contabil
		where	nr_seq_mes_ref	= nr_sequencia_p
		and	coalesce(dt_atualizacao_saldo::text, '') = ''
		and	cd_estabelecimento = cd_estabelecimento_p;

		if (qt_referencia_w = 0) then
			begin
			insert into ctb_mes_ref_estab(
				nr_sequencia,
				cd_estabelecimento,
				nr_seq_mes,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				dt_fechamento,
				nm_usuario_fechamento)
			values (nextval('ctb_mes_ref_estab_seq'),
				cd_estabelecimento_p,
				nr_sequencia_p,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p);
			end;
		else
			CALL wheb_mensagem_pck.exibir_mensagem_abort(202923,
			'NR_SEQ_LOTE_W=' || nr_seq_lote_w ||
			';CD_ESTABELECIMENTO_W='	|| cd_estabelecimento_w);
		end if;

		nr_seq_log_w := ctb_grava_log_mes(nr_seq_log_w, nm_usuario_p, nr_sequencia_p, '4', WHEB_MENSAGEM_PCK.get_texto(303739, 'NM_ESTABELECIMENTO_W=' || nm_estabelecimento_w));

		end;
	else
		delete from ctb_mes_ref_estab
		where	nr_seq_mes = nr_sequencia_p 
		and	cd_estabelecimento = cd_estabelecimento_p;

		nr_seq_log_w := ctb_grava_log_mes(nr_seq_log_w, nm_usuario_p, nr_sequencia_p, '3', WHEB_MENSAGEM_PCK.get_texto(303739, 'NM_ESTABELECIMENTO_W=' || nm_estabelecimento_w));
	end if;

end if;	
commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE ctb_abrir_fechar_mes_estab ( nr_sequencia_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) FROM PUBLIC;

