SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ctb_acumular_demo ( nr_seq_demo_p bigint, nm_usuario_p text) AS $body$
DECLARE



nr_sequencia_w			bigint;
nr_seq_tipo_w			bigint;
nr_seq_mes_ref_w			bigint;
ds_origem_w			varchar(4000);
vl_referencia_w			double precision;
nr_seq_col_w			bigint;
ie_origem_valor_w			varchar(3);

c01 CURSOR FOR
SELECT	distinct
	nr_seq_mes_ref,
	nr_seq_col
from	ctb_demo_rubrica
where	nr_seq_demo	= nr_seq_demo_p;

c02 CURSOR FOR
SELECT	a.nr_sequencia,
	ds_origem,
	b.ie_origem_valor
from	ctb_modelo_rubrica b,
	ctb_demo_rubrica a
where	a.nr_seq_demo	= nr_seq_demo_p
and	a.nr_seq_mes_ref	= nr_seq_mes_ref_w
and	a.nr_seq_col	= nr_seq_col_w
and	b.ie_origem_valor	in ('SR','FR')
and	a.nr_seq_rubrica	= b.nr_sequencia
order by nr_seq_somat, nr_seq_apres;


BEGIN

open	c01;
loop
fetch	c01 into
	nr_seq_mes_ref_w,
	nr_seq_col_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	open	c02;
	loop
	fetch	c02 into
		nr_sequencia_w,
		ds_origem_w,
		ie_origem_valor_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */

		if (ie_origem_valor_w = 'SR') then

			select	ctb_obter_valor_demo(
				nr_seq_demo_p,
				nr_seq_mes_ref_w,
				ds_origem_w,
				nr_seq_col_w)
			into STRICT	vl_referencia_w
			;
		elsif (ie_origem_valor_w = 'FR') then
			select	ctb_obter_valor_function(
				nr_seq_demo_p,
				nr_seq_mes_ref_w,
				ds_origem_w,
				nr_seq_col_w)
			into STRICT	vl_referencia_w
			;
		end if;
		update	ctb_demo_rubrica
		set	vl_referencia	= vl_referencia_w
		where	nr_sequencia	= nr_sequencia_w
		and	nr_seq_col	= nr_seq_col_w;

	end loop;
	close c02;
end loop;
close c01;
commit;
CALL ctb_calc_demo_var_per(nr_seq_demo_p, nm_usuario_p);
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
