SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ctb_acumular_orcamento ( nr_seq_mes_ref_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_estabelecimento_w		integer;
cd_conta_contabil_w		varchar(40);
cd_centro_custo_w			varchar(40);
cd_classificacao_w			varchar(40);
cd_empresa_w			integer;
vl_original_w			double precision;
vl_orcado_w			double precision;
vl_realizado_w			double precision;
nr_sequencia_w			bigint;
k				integer;
dt_referencia_w			timestamp;

C01 CURSOR FOR
SELECT
	a.cd_estabelecimento,
	ctb_obter_classif_conta_sup(cd_classificacao, dt_referencia_w, b.cd_empresa),
	a.cd_centro_custo,
	sum(vl_original),
	sum(vl_orcado),
	sum(vl_realizado)
from	conta_contabil b,
	ctb_orcamento a
where	a.nr_seq_mes_ref	= nr_seq_mes_ref_p
  and	a.cd_conta_contabil	= b.cd_conta_contabil
  and	K			< 7
  and	CTB_Obter_Nivel_Classif_Conta(b.cd_classificacao) = K
  and	(ctb_obter_classif_conta_sup(b.cd_classificacao, dt_referencia_w, b.cd_empresa) IS NOT NULL AND (ctb_obter_classif_conta_sup(b.cd_classificacao, dt_referencia_w, b.cd_empresa))::text <> '')
group by
	a.cd_estabelecimento,
	ctb_obter_classif_conta_sup(cd_classificacao, dt_referencia_w, b.cd_empresa),
	a.cd_centro_custo;


BEGIN

select	cd_empresa,
	dt_referencia
into STRICT	cd_empresa_w,
	dt_referencia_w
from	ctb_mes_ref
where	nr_sequencia	= nr_seq_mes_ref_p;

delete 	from ctb_orcamento
where	nr_seq_mes_ref		= nr_seq_mes_ref_p
  and	cd_conta_contabil in (
	SELECT	cd_conta_contabil
	from	conta_contabil
	where	cd_empresa		= cd_empresa_w
	  and	ie_tipo		= 'T');

k					:= 7;
WHILE k > 0 LOOP
	OPEN C01;
	LOOP
	FETCH C01 into
		cd_estabelecimento_w,
		cd_classificacao_w,
		cd_centro_custo_w,
		vl_original_w,
		vl_orcado_w,
		vl_realizado_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		select	min(cd_conta_contabil)
		into STRICT	cd_conta_contabil_w
		from	conta_contabil
		where 	cd_classificacao = cd_classificacao_w;
		if (coalesce(cd_conta_contabil_w::text, '') = '') then
			CALL wheb_mensagem_pck.exibir_mensagem_abort(225014,'cd_classificacao=' || cd_classificacao_w);
		else
			begin
			select	nextval('ctb_orcamento_seq')
			into STRICT	nr_sequencia_w
			;
			begin
			insert into ctb_orcamento(
				nr_sequencia,
				nr_seq_mes_ref,
				dt_atualizacao,
				nm_usuario,
				cd_estabelecimento,
				cd_conta_contabil,
				cd_centro_custo,
				vl_original,
				vl_orcado,
				vl_realizado)
			values (
				nr_sequencia_w,
				nr_seq_mes_ref_p,
				clock_timestamp(),
				nm_usuario_p,
				cd_estabelecimento_w,
				cd_conta_contabil_w,
				cd_centro_custo_w,
				vl_original_w,
				vl_orcado_w,
				vl_realizado_w);
			exception
				when unique_violation then
					update ctb_orcamento
					SET	vl_original		= vl_original + vl_original_w,
						vl_orcado 		= vl_orcado + vl_orcado_w,
						vl_realizado		= vl_realizado + vl_realizado_w
					where	cd_estabelecimento	= cd_estabelecimento_w
					  and	nr_seq_mes_ref	= nr_seq_mes_ref_p
					  and	cd_conta_contabil	= cd_conta_contabil_w
					  and	cd_centro_custo	= cd_centro_custo_w;
				when others then
					insert into ctb_log(
						dt_atualizacao,
						nm_usuario,
						cd_log,
						ds_log)
					values (	clock_timestamp(),
						nm_usuario_p,
						301,
						cd_conta_contabil_w);
			end;
			end;
		end if;
		end;
	END LOOP;
	CLOSE C01;
	commit;
	K	:= K - 1;
END LOOP;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
