SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ctb_alterar_data_lote ( nr_lote_contabil_p bigint, dt_referencia_p timestamp, nm_usuario_p text) AS $body$
DECLARE


dt_parametro_w			timestamp;
dt_referencia_w			timestamp;
dt_referencia_ant_w		timestamp;
ds_gerado_w			varchar(255);

c01 CURSOR FOR
	SELECT	nr_seq_parametro,
		dt_parametro
	from	lote_contabil_parametro
	where	nr_lote_contabil	= nr_lote_contabil_p
	and	(dt_parametro IS NOT NULL AND dt_parametro::text <> '')
	and	nr_seq_parametro in (1,2,3);

vet01	C01%rowtype;


BEGIN
dt_referencia_w	:= trunc(dt_referencia_p,'dd');

select	max(a.dt_referencia),
	substr(CASE WHEN coalesce(max(a.dt_inicio_geracao)::text, '') = '' THEN 'N'  ELSE 'S' END ,1,255)
into STRICT	dt_referencia_ant_w,
	ds_gerado_w
from	lote_contabil	a
where	a.nr_lote_contabil	= nr_lote_contabil_p;

update	lote_contabil
set	dt_referencia		= dt_referencia_w,
	nm_usuario		= nm_usuario_p,
	dt_atualizacao		= clock_timestamp()
where	nr_lote_contabil	= nr_lote_contabil_p;

open C01;
loop
fetch C01 into
	vet01;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	begin
	dt_parametro_w	:= to_date(to_char(dt_referencia_w, 'dd/mm/yyyy') ||' '|| to_char(vet01.dt_parametro,'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss');

	update	lote_contabil_parametro
	set	dt_parametro 		= dt_parametro_w,
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_lote_contabil	= nr_lote_contabil_p
	and	(dt_parametro IS NOT NULL AND dt_parametro::text <> '')
	and	nr_seq_parametro	= vet01.nr_seq_parametro;
	exception when others then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(184609,
		'DS_ERRO_W=' ||dt_parametro_w);
	end;
	end;
end loop;
close C01;

insert into lote_contabil_log(cd_log_lote,
	ds_log,
	dt_atualizacao,
	dt_contabil,
	nm_atributo,
	nm_tabela,
	nm_usuario,
	nr_documento,
	nr_lote_contabil,
	nr_seq_movimento,
	nr_seq_trans_fin,
	nr_sequencia,
	vl_movimento)
values (11,
	substr(wheb_mensagem_pck.get_texto(317976,	'DT_ALTERACAO_ANT=' || dt_referencia_ant_w || ';' ||
						'DT_ALTERACAO_PST=' || dt_referencia_w || ';' ||
						'IE_LOTE_GERADO=' || ds_gerado_w ),1,4000),
	clock_timestamp(),
	null,
	null,
	null,
	nm_usuario_p,
	null,
	nr_lote_contabil_p,
	null,
	null,
	nextval('lote_contabil_log_seq'),
	null);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
