CREATE OR REPLACE PROCEDURE ctb_aplicar_regra_metrica ( nr_seq_cenario_p bigint, nr_sequencia_p bigint, cd_centro_custo_p bigint, nr_seq_mes_ref_p bigint, nr_seq_metrica_p bigint, dt_mes_inic_p timestamp, dt_mes_fim_p timestamp, ie_regra_p text, pr_aplicar_p bigint, ie_sobrepor_p text, qt_fixa_p bigint, qt_leito_p bigint, tx_ocupacao_p bigint, cd_empresa_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


/*
ie_regra_valor - dominio 1718
			QF            Quantidade fixa
			PO            Percentual adicional da origem
			PQ            Percentual da quantidade
*/
nr_dias_mes_w				smallint;
nr_seq_mes_ref_w			bigint;
nr_sequencia_w			bigint;
ds_erro_w				varchar(2000);
dt_referencia_w			timestamp;
qt_reg_w				bigint;
qt_metrica_w				double precision;
qt_metrica_ww				double precision;

C01 CURSOR FOR

/* Quantidade fixa */

	SELECT	qt_fixa_p,
		a.nr_sequencia nr_seq_mes_ref
	from	ctb_mes_ref a
	where	ie_regra_p			in ('QF','OC')
	and	a.nr_sequencia in (
		SELECT	nr_sequencia
		from	ctb_mes_ref
		where	dt_referencia between trunc(dt_mes_inic_p,'month') and trunc(dt_mes_fim_p,'month')
		and	cd_empresa			= cd_empresa_p)
	and	coalesce(nr_seq_mes_ref_p::text, '') = ''
	
union all

/* Percentual de origem */

	select	qt_metrica_ww,
		nr_sequencia nr_seq_mes_ref
	from	ctb_mes_ref
	where	ie_regra_p			= 'PO'
	and	dt_referencia between trunc(dt_mes_inic_p,'month') and trunc(dt_mes_fim_p,'month')
	and	cd_empresa			= cd_empresa_p
	
union all

/* Percentual da quantidade */

	select	a.qt_metrica,
		a.nr_seq_mes_ref
	from	ctb_mes_ref b,
		ctb_cen_metrica a
	where	a.nr_seq_cenario		= nr_seq_cenario_p
	and	nr_seq_metrica		= nr_seq_metrica_p
	and	a.cd_centro_custo 		= cd_centro_custo_p
	and	coalesce(nr_seq_mes_ref_p::text, '') = ''
	and	ie_regra_p			= 'PQ'
	and	nr_seq_mes_ref		= b.nr_sequencia
	and	b.dt_referencia between trunc(dt_mes_inic_p,'month') and trunc(dt_mes_fim_p,'month')
	and	b.cd_empresa			= cd_empresa_p;



BEGIN

if (ie_regra_p = 'PO') then
	select	coalesce(max(qt_metrica),0)
	into STRICT	qt_metrica_ww
	from	ctb_cen_metrica
	where	nr_seq_cenario		= nr_seq_cenario_p
	and	nr_seq_metrica		= nr_seq_metrica_p
	and	cd_centro_custo 		= cd_centro_custo_p
	and	nr_seq_mes_ref		= nr_seq_mes_ref_p;
end if;

OPEN C01;
LOOP
FETCH c01 into
	qt_metrica_w,
	nr_seq_mes_ref_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	if (ie_regra_p = 'PO') then
		select	coalesce(sum(qt_metrica),0)
		into STRICT	qt_metrica_w
		from 	ctb_cen_metrica
		where 	nr_seq_cenario	= nr_seq_cenario_p
		and	nr_seq_mes_ref	= nr_seq_mes_ref_p
		and	nr_seq_metrica	= nr_seq_metrica_p
		and	cd_centro_custo	= cd_centro_custo_p;
	end if;
	if (ie_regra_p = 'PO') then
		qt_metrica_w	:= qt_metrica_w + ((qt_metrica_w * pr_aplicar_p) / 100);
	elsif (ie_regra_p = 'PQ') then
		qt_metrica_w	:= qt_metrica_w * (pr_aplicar_p / 100);
	elsif (ie_regra_p = 'OC') then

		select	max(dt_referencia)
		into STRICT	dt_referencia_w
		from	ctb_mes_ref
		where	nr_sequencia	= nr_seq_mes_ref_w;

		nr_dias_mes_w	:= somente_numero(to_char(last_day(dt_referencia_w),'DD'));

		qt_metrica_w	:= trunc(((nr_dias_mes_w * qt_leito_p) * (dividir(tx_ocupacao_p, 100))));
	end if;

	select	count(*)
	into STRICT	qt_reg_w
	from 	ctb_cen_metrica
	where 	nr_seq_cenario	= nr_seq_cenario_p
	and	nr_seq_mes_ref	= nr_seq_mes_ref_w
	and	nr_seq_metrica	= nr_seq_metrica_p
	and	cd_centro_custo	= cd_centro_custo_p;
	if (qt_reg_w = 0)	then
		select	nextval('ctb_cen_metrica_seq')
		into STRICT	nr_sequencia_w
		;
		insert into ctb_cen_metrica(
			nr_sequencia,
			nr_seq_cenario,
			cd_estabelecimento,
			nr_seq_mes_ref,
			dt_atualizacao,
			nm_usuario,
			nr_seq_metrica,
			cd_centro_custo,
			qt_metrica)
		values (
			nr_sequencia_w,
			nr_seq_cenario_p,
			cd_estabelecimento_p,
			nr_seq_mes_ref_w,
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_metrica_p,
			cd_centro_custo_p,
			qt_metrica_w);
	elsif (ie_sobrepor_p = 'S') then
		update	ctb_cen_metrica
		set	dt_atualizacao	= clock_timestamp(),
			nm_usuario		= nm_usuario_p,
			qt_metrica		= qt_metrica_w
		where 	nr_seq_cenario	= nr_seq_cenario_p
		and	nr_seq_mes_ref 	= nr_seq_mes_ref_w
		and	nr_seq_metrica 	= nr_seq_metrica_p
		and	cd_centro_custo 	= cd_centro_custo_p
		and	cd_estabelecimento	= cd_estabelecimento_p;
	end if;
END LOOP;
CLOSE C01;

COMMIT;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE ctb_aplicar_regra_metrica ( nr_seq_cenario_p bigint, nr_sequencia_p bigint, cd_centro_custo_p bigint, nr_seq_mes_ref_p bigint, nr_seq_metrica_p bigint, dt_mes_inic_p timestamp, dt_mes_fim_p timestamp, ie_regra_p text, pr_aplicar_p bigint, ie_sobrepor_p text, qt_fixa_p bigint, qt_leito_p bigint, tx_ocupacao_p bigint, cd_empresa_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) FROM PUBLIC;

