CREATE OR REPLACE PROCEDURE ctb_aprovar_planej_orc ( nr_seq_planej_orc_p bigint, ie_operacao_p text, nm_usuario_p text) AS $body$
BEGIN

if (ie_operacao_p = 'A') then
    update  ctb_orc_cenario
    set     dt_aprovacao = clock_timestamp(),
            dt_atualizacao = clock_timestamp(),
            nm_usuario = nm_usuario_p
    where   nr_sequencia = nr_seq_planej_orc_p;
elsif (ie_operacao_p = 'D') then
    update  ctb_orc_cenario
    set     dt_aprovacao  = NULL,
            dt_atualizacao = clock_timestamp(),
            nm_usuario = nm_usuario_p
    where   nr_sequencia = nr_seq_planej_orc_p;
end if;
commit;
END;
