CREATE OR REPLACE PROCEDURE ctb_atualizar_classif_conta ( cd_conta_contabil_p text, nm_usuario_p text) AS $body$
DECLARE


cd_classificacao_atual_w		varchar(40);
cd_classif_superior_w			varchar(40);
cd_empresa_w				bigint;



BEGIN

select	max(cd_empresa)
into STRICT	cd_empresa_w
from	conta_contabil
where	cd_conta_contabil	= cd_conta_Contabil_p;


cd_classificacao_atual_w	:= substr(ctb_obter_classif_conta(cd_conta_contabil_p, null, clock_timestamp()),1,40);
cd_classif_superior_w	:= substr(ctb_obter_classif_conta_sup(cd_classificacao_atual_w, clock_timestamp(), cd_empresa_w),1,40);


update	conta_contabil
set		cd_classificacao_atual		= cd_classificacao_atual_w,
		cd_classif_superior_atual	= cd_classif_superior_w
where	cd_conta_contabil			= cd_conta_contabil_p;

commit;

end;
