SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ctb_atualizar_classif_saldo ( cd_empresa_p bigint, cd_estab_p bigint, nr_seq_mes_ref_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_classif_nova_w			varchar(40);
cd_classif_sup_nova_w		varchar(40);
ds_erro_w			varchar(500);
ds_erro_ww			varchar(2000);
dt_referencia_w			timestamp;
nr_nivel_conta_w		bigint;

c01 CURSOR FOR
SELECT	a.nr_sequencia,
	a.cd_conta_contabil,
	a.cd_classificacao,
	a.cd_classif_sup
from	ctb_saldo a
where	a.nr_seq_mes_ref		= nr_seq_mes_ref_p
and	a.cd_estabelecimento	= coalesce(cd_estab_p, a.cd_estabelecimento);

vet01	C01%RowType;


BEGIN

dt_referencia_w	:= ctb_obter_mes_ref(nr_seq_mes_ref_p);

open C01;
loop
fetch C01 into
	vet01;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	cd_classif_nova_w	:= ctb_obter_classif_conta(vet01.cd_conta_contabil, null, dt_referencia_w);
	cd_classif_sup_nova_w	:= ctb_obter_classif_conta_sup(cd_classif_nova_w, dt_referencia_w, cd_empresa_p);
	nr_nivel_conta_w	:= CTB_Obter_Nivel_Classif_Conta(cd_classif_nova_w);

	if	((coalesce(vet01.cd_classificacao,'X') <> cd_classif_nova_w) or (coalesce(vet01.cd_classif_sup,'X') <> cd_classif_sup_nova_w)) then
		begin
		update	ctb_saldo
		set	cd_classificacao	= cd_classif_nova_w,
			cd_classif_sup	= cd_classif_sup_nova_w,
			nr_nivel_conta	= nr_nivel_conta_w
		where	nr_sequencia	= vet01.nr_sequencia;
		exception when others then
			ds_erro_w	:= substr(sqlerrm(SQLSTATE),1,500);
			ds_erro_ww	:= substr(cd_classif_nova_w || ' / ' || vet01.cd_conta_contabil ||
							chr(13) || chr(10) ||ds_erro_w,1,2000);
			CALL wheb_mensagem_pck.exibir_mensagem_abort(266403,'DS_ERRO_W=' || ds_erro_ww);
		end;
	end if;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
