SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ctb_atualizar_obs_lote ( nr_lote_contabil_p bigint, nr_seq_parametro_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_convenio_w				bigint;
cd_tipo_lote_contabil_w			bigint;
ds_observacao_w				varchar(255);


BEGIN

select	max(cd_tipo_lote_contabil)
into STRICT	cd_tipo_lote_contabil_w
from	lote_contabil
where	nr_lote_contabil	= nr_lote_contabil_p;

if (coalesce(cd_tipo_lote_contabil_w,0) = 6) then
	begin
	ds_observacao_w		:= '';

	select	coalesce(max(vl_parametro),0)
	into STRICT	cd_convenio_w
	from	lote_contabil_parametro
	where	nr_seq_parametro = nr_seq_parametro_p
	and	nr_lote_contabil = nr_lote_contabil_p;

	select	coalesce(max(cd_convenio),0)
	into STRICT	cd_convenio_w
	from	convenio
	where	cd_convenio	= cd_convenio_w;

	if (cd_convenio_w <> 0) then
		ds_observacao_w	:= substr(obter_nome_convenio(cd_convenio_w),1,255);
	end if;

	update	lote_contabil
	set	ds_observacao		= ds_observacao_w
	where	nr_lote_contabil	= nr_lote_contabil_p;

	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
