SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ctb_atualizar_rateio_movto ( nr_sequencia_p bigint, nm_usuario_p text, ie_commit_p text default 'S') AS $body$
DECLARE


nr_sequencia_w		bigint;
qt_movto_w		bigint;
pr_diferenca_w		double precision;
pr_rateio_w		double precision;
pr_total_rateio_w	double precision	:= 0;
vl_movto_w		double precision;
vl_movimento_w		double precision;

c01 CURSOR FOR
SELECT	a.nr_sequencia,
	a.vl_movimento
from	ctb_movto_centro_custo a
where	a.nr_seq_movimento	= nr_sequencia_p
order by 1;


BEGIN

/* Verifica se existem rateios por centro de custo deste movimento */

select	count(*)
into STRICT	qt_movto_w
from	ctb_movto_centro_custo
where	nr_seq_movimento	= nr_sequencia_p;

/* Valor total do movimento contabil */

select	coalesce(max(vl_movimento),0)
into STRICT	vl_movimento_w
from	ctb_movimento
where	nr_sequencia	= nr_sequencia_p;

if (qt_movto_w > 0) and (vl_movimento_w <> 0) then
	open C01;
	loop
	fetch C01 into	
		nr_sequencia_w,
		vl_movto_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		pr_rateio_w		:= dividir((vl_movto_w * 100),vl_movimento_w);
		
		update	ctb_movto_centro_custo
		set	pr_rateio 	= pr_rateio_w
		where	nr_sequencia	= nr_sequencia_w;
		
		pr_total_rateio_w	:= pr_total_rateio_w + pr_rateio_w;
		
		end;
	end loop;
	close C01;
	pr_diferenca_w		:= abs(pr_total_rateio_w - 100);
	
	if (pr_total_rateio_w > 100) then
		update	ctb_movto_centro_custo
		set	pr_rateio	= pr_rateio - pr_diferenca_w
		where	nr_sequencia	= nr_sequencia_w;
	elsif (pr_total_rateio_w < 100) then
		update	ctb_movto_centro_custo
		set	pr_rateio	= pr_rateio + pr_diferenca_w
		where	nr_sequencia	= nr_sequencia_w;
	end if;
	
end if;

if (ie_commit_p = 'S') then
commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
