SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ctb_atualizar_reversao_lote ( nr_lote_contabil_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_lote_contabil_w			bigint;
qt_registro_w			bigint;


BEGIN

select	max(a.nr_lote_contabil)
into STRICT	nr_lote_contabil_w
from	ctb_movimento a,
	ctb_movimento b
where	a.nr_sequencia	= b.nr_seq_movto_corresp
and	b.nr_lote_contabil	= nr_lote_contabil_p;

select	count(*)
into STRICT	qt_registro_w
from	ctb_movimento a
where	a.nr_lote_contabil	= nr_lote_contabil_w
and	not exists (	SELECT	1
		from	ctb_movimento y
		where	y.nr_seq_movto_corresp = a.nr_sequencia);

if (qt_registro_w > 0) then

	update	lote_contabil
	set	dt_reversao	 = NULL
	where	nr_lote_contabil	= nr_lote_contabil_w;
else
	update	lote_contabil
	set	dt_reversao	= clock_timestamp()
	where	nr_lote_contabil	= nr_lote_contabil_w;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
