CREATE OR REPLACE PROCEDURE ctb_atualizar_valor_movto ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


/*
Atualizar o valor dos movimentos de acordo com o percentual de rateio
do valor anterior.
*/
nr_sequencia_w		bigint;
qt_movto_w		bigint;
vl_diferenca_w		double precision;
pr_rateio_w		double precision;
vl_total_rateio_w	double precision	:= 0;
vl_movto_w		double precision;
vl_movimento_w		double precision;

c01 CURSOR FOR
SELECT	a.nr_sequencia,
	a.pr_rateio
from	ctb_movto_centro_custo a
where	a.nr_seq_movimento	= nr_sequencia_p
order by
	2,
	1;


BEGIN

/* Verifica se existem rateios por centro de custo deste movimento */

select	count(*)
into STRICT	qt_movto_w
from	ctb_movto_centro_custo
where	nr_seq_movimento	= nr_sequencia_p;

/* Valor total do movimento contÃ¡bil */

select	coalesce(max(vl_movimento),0)
into STRICT	vl_movimento_w
from	ctb_movimento
where	nr_sequencia	= nr_sequencia_p;

if (qt_movto_w > 0) and (vl_movimento_w <> 0) then
	begin
	open C01;
	loop
	fetch C01 into
		nr_sequencia_w,
		pr_rateio_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		vl_movto_w := ((vl_movimento_w * pr_rateio_w) / 100);

		update	ctb_movto_centro_custo
		set	vl_movimento 	= vl_movto_w
		where	nr_sequencia	= nr_sequencia_w;

		vl_total_rateio_w	:= vl_total_rateio_w + vl_movto_w;

		end;
	end loop;
	close C01;

	if (vl_total_rateio_w <> vl_movimento_w) then
		update	ctb_movto_centro_custo
		set	vl_movimento	= vl_movimento + vl_movimento_w - vl_total_rateio_w
		where	nr_sequencia	= nr_sequencia_w;
	end if;
	end;
end if;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
