CREATE OR REPLACE PROCEDURE ctb_atualiza_ccusto_movto ( nr_sequencia_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_conta_debito_w			varchar(20);
cd_conta_credito_w		varchar(20);
nr_seq_crit_rateio_w		bigint;
cd_centro_custo_w			integer;
vl_movimento_w			ctb_movimento.vl_movimento%type;
dt_movimento_w			timestamp;
cd_estab_centro_w		centro_custo.cd_estabelecimento%type;


BEGIN

select	max(cd_conta_debito),
	max(cd_conta_credito),
	max(vl_movimento),
	max(dt_movimento)
into STRICT	cd_conta_debito_w,
	cd_conta_credito_w,
	vl_movimento_w,
	dt_movimento_w
from	ctb_movimento
where	nr_sequencia	= nr_sequencia_p;

select	coalesce(max(obter_dados_conta_contabil(cd_conta_contabil,cd_estabelecimento_p,'CR')),0),
	coalesce(max(obter_dados_conta_contabil(cd_conta_contabil,cd_estabelecimento_p,'CC')),0)
into STRICT	nr_seq_crit_rateio_w,
	cd_centro_custo_w
from	conta_contabil
where	cd_conta_contabil in (cd_conta_debito_w, cd_conta_credito_w);

if (nr_seq_crit_rateio_w > 0) then
	CALL ctb_rateio_lancamento(nr_sequencia_p, nr_seq_crit_rateio_w, nm_usuario_p, dt_movimento_w);
elsif (cd_centro_custo_w > 0) then
	begin
	delete	FROM ctb_movto_centro_custo
	where	nr_seq_movimento = nr_sequencia_p;

	insert into ctb_movto_centro_custo(
		nr_sequencia,
		nr_seq_movimento,
		cd_centro_custo,
		dt_atualizacao,
		nm_usuario,
		vl_movimento,
		pr_rateio)
	values (nextval('ctb_movto_centro_custo_seq'),
		nr_sequencia_p,
		cd_centro_custo_w,
		clock_timestamp(),
		nm_usuario_p,
		vl_movimento_w,
		100);
		
	select cd_estabelecimento
	into STRICT cd_estab_centro_w
	from centro_custo
	where cd_centro_custo = cd_centro_custo_w;
	
	update ctb_movimento
	set cd_estabelecimento = cd_estab_centro_w
	where nr_sequencia = nr_sequencia_p;
	commit;
	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
