CREATE OR REPLACE PROCEDURE ctb_calcular_metrica_real ( cd_estabelecimento_p bigint, nr_seq_mes_ref_p bigint, nm_usuario_p text, ie_todos_centros_p bigint, cd_centro_custo_p bigint, nr_seq_metrica_p bigint, nr_sequencia_p bigint) AS $body$
DECLARE


cd_centro_custo_w			bigint;
ie_calculo_metrica_real_w		varchar(15);
nr_seq_metrica_w			bigint;
nr_sequencia_w			bigint;
qt_metrica_real_w			double precision;
qt_registro_w			bigint;

c01 CURSOR FOR
SELECT	a.nr_sequencia,
	b.cd_centro_custo
from	ctb_orc_metrica b,
	ctb_metrica a
where	a.nr_sequencia	= b.nr_seq_metrica
and	not exists (		SELECT	1
			from	ctb_metrica_real y
			where	y.nr_seq_metrica		= a.nr_sequencia
			and	y.cd_centro_custo		= b.cd_centro_custo
			and	y.cd_estabelecimento		= cd_estabelecimento_p
			and	y.nr_seq_mes_ref		= nr_seq_mes_ref_p);

c02 CURSOR FOR
SELECT	a.nr_sequencia,
	a.nr_seq_metrica,
	a.cd_centro_custo,
	b.ie_calculo_metrica_real
from	ctb_metrica b,
	ctb_metrica_real a
where	a.nr_seq_metrica		= b.nr_sequencia
and	a.nr_seq_mes_ref		= nr_seq_mes_ref_p
and	b.ie_calculo_metrica_real	<> 'I';

C03 CURSOR FOR
	SELECT	a.nr_sequencia
	from	ctb_orc_metrica b,
		ctb_metrica a
	where	a.nr_sequencia	= b.nr_seq_metrica
	and	not exists (		SELECT	1
				from	ctb_metrica_real y
				where	y.nr_seq_metrica		= a.nr_sequencia
				and	y.cd_centro_custo		= b.cd_centro_custo
				and	y.cd_estabelecimento		= cd_estabelecimento_p
				and	y.nr_seq_mes_ref		= nr_seq_mes_ref_p);

C04 CURSOR FOR
	SELECT	a.nr_sequencia,
		a.nr_seq_metrica,
		b.ie_calculo_metrica_real
	from	ctb_metrica b,
		ctb_metrica_real a
	where	a.nr_seq_metrica		= b.nr_sequencia
	and	a.nr_seq_mes_ref		= nr_seq_mes_ref_p
	and	b.ie_calculo_metrica_real	<> 'I';

BEGIN

if (ie_todos_centros_p = 0) then
open C01;
loop
fetch C01 into
	nr_seq_metrica_w,
	cd_centro_custo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	insert into ctb_metrica_real(
		nr_sequencia,
		cd_estabelecimento,
		nr_seq_mes_ref,
		nm_usuario,
		dt_atualizacao,
		nm_usuario_nrec,
		dt_atualizacao_nrec,
		cd_centro_custo,
		nr_seq_metrica,
		qt_metrica_real)
	values (	nextval('ctb_metrica_real_seq'),
		cd_estabelecimento_p,
		nr_seq_mes_ref_p,
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		cd_centro_custo_w,
		nr_seq_metrica_w,
		0);

	end;
end loop;
close C01;
end if;

if (ie_todos_centros_p = 1) then


select	count(*)
into STRICT	qt_registro_w
from	ctb_orc_metrica b,
		ctb_metrica a
where	a.nr_sequencia	= b.nr_seq_metrica
and		not exists (		SELECT	1
						from	ctb_metrica_real y
						where	y.nr_seq_metrica		= a.nr_sequencia
						and	y.cd_centro_custo			= b.cd_centro_custo
						and	y.cd_estabelecimento		= cd_estabelecimento_p
						and	y.nr_seq_mes_ref			= nr_seq_mes_ref_p
						and	cd_centro_custo 			= cd_centro_custo_p
						and	nr_seq_metrica  			= nr_seq_metrica_p);

	if (qt_registro_w = 0) then
	insert into 	ctb_metrica_real(
			nr_sequencia,
			cd_estabelecimento,
			nr_seq_mes_ref,
			nm_usuario,
			dt_atualizacao,
			nm_usuario_nrec,
			dt_atualizacao_nrec,
			cd_centro_custo,
			nr_seq_metrica,
			qt_metrica_real)
		values (	nextval('ctb_metrica_real_seq'),
			cd_estabelecimento_p,
			nr_seq_mes_ref_p,
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			cd_centro_custo_p,
			nr_seq_metrica_p,
			0);
	end if;
end if;

if (ie_todos_centros_p = 0) then
open C02;
loop
fetch C02 into
	nr_sequencia_w,
	nr_seq_metrica_w,
	cd_centro_custo_w,
	ie_calculo_metrica_real_w;
EXIT WHEN NOT FOUND; /* apply on C02 */
	begin
	qt_metrica_real_w	:= 0;

	if (ie_calculo_metrica_real_w = 'S') then
		begin
		qt_metrica_real_w := ctb_calcular_metrica_sql( 	nr_seq_metrica_w, cd_estabelecimento_p, cd_centro_custo_w, nr_seq_mes_ref_p, qt_metrica_real_w);

		end;
	end if;

	update	ctb_metrica_real
	set	qt_metrica_real	= qt_metrica_real_w,
		dt_atualizacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p
	where	nr_sequencia	= nr_sequencia_w;

	end;
end loop;
close C02;
end if;

if (ie_todos_centros_p = 1) then

	qt_metrica_real_w	:= 0;

	qt_metrica_real_w := ctb_calcular_metrica_sql( 	nr_seq_metrica_p, cd_estabelecimento_p, cd_centro_custo_p, nr_seq_mes_ref_p, qt_metrica_real_w);


	update	ctb_metrica_real
	set	qt_metrica_real	= qt_metrica_real_w,
		dt_atualizacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p
	where	nr_sequencia	= nr_sequencia_p;
end if;

commit;

end;
$body$
