SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ctb_calcular_metrica_sql ( nr_seq_metrica_p bigint, cd_estabelecimento_p bigint, cd_centro_custo_p bigint, nr_seq_mes_ref_p bigint, qt_metrica_real_p INOUT bigint) AS $body$
DECLARE


dt_inicial_w			timestamp;
dt_final_w			timestamp;
dt_referencia_w			timestamp;
ds_parametros_sql_w		varchar(255);
ds_sql_w				varchar(4000);
nr_seq_regra_calc_w		bigint;
qt_real_w				double precision;
cd_empresa_w			integer;


BEGIN

select	max(dt_referencia)
into STRICT	dt_referencia_w
from	ctb_mes_ref
where	nr_sequencia	= nr_seq_mes_ref_p;

dt_referencia_w	:= trunc(dt_referencia_w,'mm');
dt_inicial_w	:= dt_referencia_w;
dt_final_w	:= fim_mes(dt_referencia_w);
cd_empresa_w	:= obter_empresa_estab(cd_estabelecimento_p);


/* Obter o comando SQL*/

select	max(nr_sequencia)
into STRICT	nr_seq_regra_calc_w
from	ctb_orc_metrica_calc
where	nr_seq_metrica		= nr_seq_metrica_p
and	dt_inicio_vigencia	<= dt_referencia_w
and	coalesce(dt_fim_vigencia, dt_referencia_w) >= dt_referencia_w;

select	max(ds_sql)
into STRICT	ds_sql_w
from	ctb_orc_metrica_calc
where	nr_sequencia	= nr_seq_regra_calc_w;

if (ds_sql_w IS NOT NULL AND ds_sql_w::text <> '') then
	begin
	if	((position(':DT_REFERENCIA' in upper(ds_sql_w))) > 0) then
		ds_parametros_sql_w	:= 'dt_referencia=' || dt_referencia_w || ';';
	end if;
	if	((position(':DT_INICIAL' in upper(ds_sql_w))) > 0) then
		ds_parametros_sql_w	:= ds_parametros_sql_w || 'dt_inicial=' || dt_inicial_w || ';';
	end if;
	if	((position(':DT_FINAL' in upper(ds_sql_w))) > 0) then
		ds_parametros_sql_w	:= ds_parametros_sql_w || 'dt_final=' || dt_final_w || ';';
	end if;
	if	((position(':CD_ESTABELECIMENTO' in upper(ds_sql_w))) > 0) then
		ds_parametros_sql_w	:= ds_parametros_sql_w || 'cd_estabelecimento=' || cd_estabelecimento_p || ';';
	end if;
	if	((position(':CD_EMPRESA' in upper(ds_sql_w))) > 0) then
		ds_parametros_sql_w	:= ds_parametros_sql_w || 'cd_empresa=' || cd_empresa_w || ';';
	end if;
	if	((position(':CD_CENTRO_CUSTO' in upper(ds_sql_w))) > 0) then
		ds_parametros_sql_w	:= ds_parametros_sql_w || 'cd_centro_custo=' || cd_centro_custo_p || ';';
	end if;
	end;
end if;

qt_real_w := obter_valor_dinamico_bv(ds_sql_w, ds_parametros_sql_w, qt_real_w);
qt_metrica_real_p	:= coalesce(qt_real_w,0);

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
