SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ctb_calc_demo_var_per ( nr_seq_demo_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w			bigint;
nr_seq_rubrica_w			bigint;
nr_seq_col_w			bigint;
nr_seq_col_1_w			smallint;
nr_seq_col_2_w			smallint;
nr_seq_col_1_ww			bigint;
nr_seq_col_2_ww			bigint;
vl_referencia_w			double precision;
vl_ref_1_w			double precision;
vl_ref_2_w			double precision;
ie_valor_w			varchar(03);
nr_seq_colunas_w			varchar(255);
ds_seq_coluna_w			varchar(2000);
ie_sep_coluna_w			integer;
nr_seq_coluna_w			bigint;
nr_seq_coluna_ww			bigint;
vl_referencia_ww			double precision;
qt_coluna_w			double precision	:= 0;

C01 CURSOR FOR
SELECT	nr_sequencia,
	ie_valor,
	nr_seq_col_1,
	nr_seq_col_2,
	nr_seq_colunas
from	ctb_demo_mes
where	nr_seq_demo	= nr_seq_demo_p
and	ie_valor	in ('V','P','SM','SV','ME');

C02 CURSOR FOR
SELECT	nr_sequencia,
	nr_seq_rubrica
from	ctb_demo_rubrica
where	nr_seq_demo		= nr_seq_demo_p
and	nr_seq_col		= nr_seq_col_w
order	by nr_sequencia;


BEGIN

OPEN	C01;
LOOP
FETCH	C01 into
	nr_seq_col_w,
	ie_valor_w,
	nr_seq_col_1_w,
	nr_seq_col_2_w,
	nr_seq_colunas_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	if (ie_valor_w  ('V','P','SM')) then
		begin
		select	min(nr_sequencia)
		into STRICT	nr_seq_col_1_ww
		from	ctb_demo_mes b
		where	b.nr_seq_demo	= nr_seq_demo_p
		and	b.nr_seq_coluna	= nr_seq_col_1_w;

		select	min(nr_sequencia)
		into STRICT	nr_seq_col_2_ww
		from	ctb_demo_mes b
		where	b.nr_seq_demo	= nr_seq_demo_p
		and	b.nr_seq_coluna	= nr_seq_col_2_w;
		end;
	end if;


	OPEN	C02;
	LOOP
	FETCH	C02 into
		nr_sequencia_w,
		nr_seq_rubrica_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */
		begin
		qt_coluna_w	:= 0;
		if (ie_valor_w in ('V','P','SM')) then
			begin
			select	coalesce(sum(vl_referencia),0)
			into STRICT	vl_ref_1_w
			from	ctb_demo_rubrica a
			where	a.nr_seq_demo	= nr_seq_demo_p
			and	a.nr_seq_rubrica	= nr_seq_rubrica_w
			and	nr_seq_col 	= nr_seq_col_1_ww;

			select	coalesce(sum(vl_referencia),0)
			into STRICT	vl_ref_2_w
			from	ctb_demo_rubrica a
			where	a.nr_seq_demo	= nr_seq_demo_p
			and	a.nr_seq_rubrica	= nr_seq_rubrica_w
			and	nr_seq_col 	= nr_seq_col_2_ww;

			vl_referencia_w		:= vl_ref_2_w - vl_ref_1_w;
			if (ie_valor_w	= 'P') then
				vl_referencia_w	:= dividir(vl_referencia_w * 100, vl_ref_1_w);
			elsif (ie_valor_w	= 'SM') then
				vl_referencia_w	:= vl_ref_2_w + vl_ref_1_w;
			end if;
			end;

		elsif (ie_valor_w in ('SV','ME')) then
			begin
			vl_referencia_w	:= 0;
			ds_seq_coluna_w	:= nr_seq_colunas_w || ';';
			ie_sep_coluna_w	:= position(';' in ds_seq_coluna_w);
			nr_seq_coluna_w	:= (coalesce(substr(ds_seq_coluna_w,1,ie_sep_coluna_w-1),'0'))::numeric;
			ds_seq_coluna_w	:= substr(ds_seq_coluna_w,ie_sep_coluna_w + 1,length(ds_seq_coluna_w));

			while	coalesce(nr_seq_coluna_w,0) <> 0 loop
				begin

				select	min(nr_sequencia)
				into STRICT	nr_seq_coluna_ww
				from	ctb_demo_mes b
				where	b.nr_seq_demo	= nr_seq_demo_p
				and	b.nr_seq_coluna	= nr_seq_coluna_w;


				select	coalesce(sum(vl_referencia),0)
				into STRICT	vl_referencia_ww
				from	ctb_demo_rubrica a
				where	a.nr_seq_demo	= nr_seq_demo_p
				and	a.nr_seq_rubrica	= nr_seq_rubrica_w
				and	a.nr_seq_col 	= nr_seq_coluna_ww;

				vl_referencia_w	:= vl_referencia_w + vl_referencia_ww;
				qt_coluna_w	:= qt_coluna_w + 1;

				ie_sep_coluna_w	:= position(';' in ds_seq_coluna_w);
				nr_seq_coluna_w	:= (coalesce(substr(ds_seq_coluna_w,1,ie_sep_coluna_w-1),'0'))::numeric;
				ds_seq_coluna_w	:= substr(ds_seq_coluna_w,ie_sep_coluna_w + 1,length(ds_seq_coluna_w));

				end;
			end loop;
			if (ie_valor_w = 'ME') then /*Média dos valores pelo numero das colunas passadas por parâmetro*/
				vl_referencia_w	:= dividir(vl_referencia_w,qt_coluna_w);
			end if;
			end;
		end if;

		update	ctb_demo_rubrica
		set	vl_referencia		= vl_referencia_w
		where	nr_sequencia		= nr_sequencia_w;

		end;
	END LOOP;
	CLOSE C02;

	end;
END LOOP;
CLOSE C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE ctb_calc_demo_var_per ( nr_seq_demo_p bigint, nm_usuario_p text) FROM PUBLIC;

