CREATE OR REPLACE PROCEDURE ctb_conciliar_movimento ( nr_sequencia_p ctb_movimento.nr_sequencia%type, ie_operacao_p text, ie_commit_p text default 'N', nm_usuario_p text DEFAULT NULL) AS $body$
BEGIN

update	ctb_movimento
set	ie_status_concil	 = CASE WHEN ie_operacao_p='C' THEN  'C'  ELSE 'NC' END ,
	dt_atualizacao	 = clock_timestamp(),
	nm_usuario	 = nm_usuario_p
where	nr_sequencia	 = nr_sequencia_p;

if (ie_commit_p = 'S') then
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
