SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ctb_consistir_classif_saldo ( nr_seq_mes_ref_p bigint, cd_estab_p bigint, nm_usuario_p text, ds_erro_p INOUT text) AS $body$
DECLARE


cd_classificacao_w			varchar(40);
cd_classif_sup_w			varchar(40);
cd_empresa_w				bigint;
ds_consistencia_w			varchar(2000);
dt_referencia_w				timestamp;
ie_classif_mudou_w			varchar(1)	:= 'N';
ie_classif_sup_mudou_w			varchar(1)	:= 'N';
ie_sem_classif_w			varchar(1)	:= 'N';
ie_sem_regra_w				varchar(1)	:= 'N';
nr_nivel_conta_w			bigint;

C01 CURSOR FOR
SELECT	a.cd_conta_contabil,
	a.cd_classificacao,
	a.cd_classif_sup,
	a.nr_nivel_conta
from	ctb_saldo a
where	a.cd_estabelecimento	= coalesce(cd_estab_p, cd_estabelecimento)
and	a.nr_seq_mes_ref	= nr_seq_mes_ref_p
group by a.cd_conta_contabil,
	a.cd_classificacao,
	a.cd_classif_sup,
	a.nr_nivel_conta;

vet01	C01%RowType;


BEGIN

ds_erro_p	:= '';
/* Consistir a Classificação das Contas Contábeis */

select	dt_referencia,
	cd_empresa
into STRICT	dt_referencia_w,
	cd_empresa_w
from	ctb_mes_ref
where	nr_sequencia	= nr_seq_mes_ref_p;



open C01;
loop
fetch C01 into
	vet01;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	cd_classificacao_w	:= ctb_obter_classif_conta(vet01.cd_conta_contabil, null, dt_referencia_w);
	cd_classif_sup_w	:= ctb_obter_classif_conta_sup(cd_classificacao_w, dt_referencia_w, cd_empresa_w);

	nr_nivel_conta_w	:= coalesce(vet01.nr_nivel_conta, ctb_obter_nivel_classif_conta(vet01.cd_classificacao));

	if (coalesce(vet01.cd_classificacao::text, '') = '') and (ie_sem_classif_w = 'N') then
		ie_sem_classif_w	:= 'S';
	end if;

	if (vet01.cd_classificacao IS NOT NULL AND vet01.cd_classificacao::text <> '') and (coalesce(cd_classificacao_w,'X') = 'X') and (ie_sem_regra_w = 'N') then
		ie_sem_regra_w		:= 'S';
	end if;

	if (vet01.cd_classificacao IS NOT NULL AND vet01.cd_classificacao::text <> '') and (cd_classificacao_w IS NOT NULL AND cd_classificacao_w::text <> '') and (cd_classificacao_w <> vet01.cd_classificacao) and (ie_classif_mudou_w = 'N') then
		ie_classif_mudou_w	:= 'S';
	end if;

	if (cd_classif_sup_w <> vet01.cd_classif_sup) and (ie_classif_sup_mudou_w = 'N') then
		ie_classif_sup_mudou_w	:= 'S';
	end if;

	end;
end loop;
close C01;

ds_consistencia_w	:= '';

if (ie_sem_classif_w = 'S') then
	ds_consistencia_w	:= WHEB_MENSAGEM_PCK.get_texto(277747);
end if;

if (ie_sem_regra_w = 'S') then
	ds_consistencia_w	:= ds_consistencia_w || WHEB_MENSAGEM_PCK.get_texto(277748);
end if;

if (ie_classif_mudou_w = 'S') then
	ds_consistencia_w	:= ds_consistencia_w || WHEB_MENSAGEM_PCK.get_texto(277749);
end if;

if (ie_classif_sup_mudou_w = 'S') then
	ds_consistencia_w	:= ds_consistencia_w ||  WHEB_MENSAGEM_PCK.get_texto(277750);
end if;

ds_erro_p	:= substr(ds_consistencia_w,1,255);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
