CREATE OR REPLACE PROCEDURE ctb_consistir_fim_vig_conta ( nm_usuario_p text, cd_estabelecimento_p bigint, cd_conta_contabil_p text, dt_inicio_vigencia_p timestamp, dt_fim_vigencia_p timestamp) AS $body$
DECLARE


nr_seq_mes_ref_w	bigint;
qt_registros_w		bigint;

c01 CURSOR FOR
	SELECT	nr_sequencia
	from	ctb_mes_ref
	where	dt_referencia > trunc(dt_fim_vigencia_p,'mm')
	order by nr_sequencia;


BEGIN

if (dt_fim_vigencia_p IS NOT NULL AND dt_fim_vigencia_p::text <> '') then
	if (coalesce(dt_inicio_vigencia_p::text, '') = '') then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(233690);
	end if;
end if;

open c01;
loop
fetch c01 into
	nr_seq_mes_ref_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	select	count(*)
	into STRICT	qt_registros_w
	from	ctb_movimento
	where	nr_seq_mes_ref		= nr_seq_mes_ref_w
	and	cd_estabelecimento	= cd_estabelecimento_p
	and	((cd_conta_debito	= cd_conta_contabil_p)
	or (cd_conta_credito	= cd_conta_contabil_p));

	if (qt_registros_w > 0) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(233685);
	end if;

	end;
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
