SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ctb_consistir_lote_geracao ( nr_lote_contabil_p bigint, nm_usuario_p text) AS $body$
DECLARE


ds_erro_w			varchar(2000);
ds_erro_ww			varchar(2000);
ds_erro_movto_w			varchar(255);
cd_conta_contabil_w		varchar(255);
ie_gravar_w			varchar(2);
vl_movimento_w			double precision;
nr_seq_movto_w			bigint;
cd_estabelecimento_w		bigint;
nr_seq_trans_fin_w		bigint;
nr_sequencia_w			bigint;
cd_empresa_w			integer;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		cd_conta_contabil,
		vl_movimento,
		nr_seq_trans_fin
	from	movimento_contabil
	where	nr_lote_contabil	= nr_lote_contabil_p;



BEGIN
ds_erro_w	:= '';
ds_erro_ww	:= '';

/*Limpa a data e as consistencias*/

delete	FROM lote_contabil_log
where	nr_lote_contabil	= nr_lote_contabil_p
and	cd_log_lote		= 10;

commit;

/* Dados do lote contabil*/

select	max(cd_estabelecimento)
into STRICT	cd_estabelecimento_w
from	lote_contabil
where	nr_lote_contabil	= nr_lote_contabil_p;

cd_empresa_w	:= obter_empresa_estab(cd_estabelecimento_w);

open C01;
loop
fetch C01 into
	nr_seq_movto_w,
	cd_conta_contabil_w,
	vl_movimento_w,
	nr_seq_trans_fin_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	begin
	ie_gravar_w	:= obter_dados_conta_contabil(cd_conta_contabil_w, cd_estabelecimento_w, 'S');
	exception
	when others then
		ie_gravar_w	:= null;
	end;

	if (ie_gravar_w = 'I') then
		select  nextval('lote_contabil_log_seq')
		into STRICT	nr_sequencia_w
		;

		ds_erro_w	:= wheb_mensagem_pck.get_texto(298035,'NR_SEQ_MOVTO_W=' || NR_SEQ_MOVTO_W || ';CD_CONTA_CONTABIL_W=' || CD_CONTA_CONTABIL_W);

		insert into lote_contabil_log(nr_sequencia,
			nm_usuario,
			dt_atualizacao,
			nr_lote_contabil,
			cd_log_lote,
			ds_log,
			nr_documento,
			nm_tabela,
			nm_atributo,
			vl_movimento,
			nr_seq_trans_fin,
			dt_contabil,
			nr_seq_movimento)
		values (nr_sequencia_w,
			nm_usuario_p,
			clock_timestamp(),
			nr_lote_contabil_p,
			10,
			ds_erro_w,
			null,
			null,
			null,
			vl_movimento_w,
			nr_seq_trans_fin_w,
			null,
			nr_seq_movto_w);
	end if;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
