CREATE OR REPLACE PROCEDURE ctb_copiar_conta_contabil_ref ( cd_empresa_origem_p bigint, cd_empresa_destino_p bigint, nm_usuario_p text) AS $body$
DECLARE


	/*Variaveis de controle*/

	nr_sequencia_w			bigint;

	/*Cursor com dados filtrados */

	c1 CURSOR FOR
	SELECT	nr_sequencia,
		clock_timestamp() dt_atualizacao,
		nm_usuario_p nm_usuario,
		cd_classificacao,
		ds_conta_referencial,
		clock_timestamp() dt_atualizacao_nrec,
		nm_usuario_p nm_usuario_nrec,
		dt_inicio_vigencia,
		dt_fim_vigencia,
		cd_declaracao,
		ie_tipo,
		cd_classif_sup,
		nr_nivel,
		ie_natureza,	
		ie_utilizacao,
		cd_versao,
		cd_empresa_destino_p cd_empresa
	from	conta_contabil_referencial
	where	cd_empresa  = cd_empresa_origem_p;

	r1 c1%rowtype;


BEGIN

open c1;
loop
fetch c1 into r1;
EXIT WHEN NOT FOUND; /* apply on c1 */

	select	nextval('conta_contabil_referencial_seq')
	into STRICT	nr_sequencia_w
	;
	
	insert into conta_contabil_referencial(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		cd_classificacao,
		ds_conta_referencial,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		dt_inicio_vigencia,
		dt_fim_vigencia,
		cd_declaracao,
		ie_tipo,
		cd_classif_sup,
		nr_nivel,
		ie_natureza,
		ie_utilizacao,
		cd_versao,
		cd_empresa)
	values (nr_sequencia_w,
		r1.dt_atualizacao,
		r1.nm_usuario,
		r1.cd_classificacao,
		r1.ds_conta_referencial,
		r1.dt_atualizacao_nrec,
		r1.nm_usuario_nrec,
		r1.dt_inicio_vigencia,
		r1.dt_fim_vigencia,
		r1.cd_declaracao,
		r1.ie_tipo,
		r1.cd_classif_sup,
		r1.nr_nivel,
		r1.ie_natureza,
		r1.ie_utilizacao,
		r1.cd_versao,
		r1.cd_empresa);

end loop;
close c1;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
