CREATE OR REPLACE PROCEDURE ctb_copiar_crit_rateio_estab ( cd_estab_origem_p bigint, cd_estab_destino_p bigint, nm_usuario_p text) AS $body$
DECLARE


	/*Variaveis de controle*/

	nr_sequencia_w			bigint;

	/*Cursor com dados filtrados */

	c1 CURSOR FOR
	SELECT	nr_sequencia
