CREATE OR REPLACE PROCEDURE ctb_copiar_demonstrativo_estab ( cd_estab_origem_p bigint, cd_estab_destino_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w				bigint;

c01 CURSOR FOR
SELECT	a.nr_sequencia,
	a.nr_seq_mes_ref,
	a.nr_seq_tipo,
	a.dt_atualizacao,
	a.nm_usuario,
	a.cd_estabelecimento,
	a.dt_atualizacao_nrec,
	a.nm_usuario_nrec,
	a.nr_seq_unid_neg,
	a.ds_titulo
from	ctb_demonstrativo a
where	a.cd_estabelecimento	= cd_estab_origem_p;

vet01	C01%RowType;


BEGIN

open C01;
loop
fetch C01 into
	vet01;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	nextval('ctb_demonstrativo_seq')
	into STRICT	nr_sequencia_w
	;

	insert	into ctb_demonstrativo(
		nr_sequencia,
		nr_seq_mes_ref,
		nr_seq_tipo,
		dt_atualizacao,
		nm_usuario,
		cd_estabelecimento,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_unid_neg,
		ds_titulo)
	values (	nr_sequencia_w,
		vet01.nr_seq_mes_ref,
		vet01.nr_seq_tipo,
		clock_timestamp(),
		nm_usuario_p,
		cd_estab_destino_p,
		clock_timestamp(),
		nm_usuario_p,
		vet01.nr_seq_unid_neg,
		vet01.ds_titulo);

	insert	into ctb_demo_mes(
		nr_sequencia,
		nr_seq_demo,
		dt_atualizacao,
		nm_usuario,
		nr_seq_mes_ref,
		nr_seq_coluna,
		ie_valor,
		nr_seq_col_1,
		nr_seq_col_2,
		ds_coluna,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		ie_informacao,
		nr_seq_colunas)
	(SELECT	nextval('ctb_demo_mes_seq'),
		nr_sequencia_w,
		clock_timestamp(),
		nm_usuario_p,
		b.nr_seq_mes_ref,
		b.nr_seq_coluna,
		b.ie_valor,
		b.nr_seq_col_1,
		b.nr_seq_col_2,
		b.ds_coluna,
		clock_timestamp(),
		nm_usuario_p,
		b.ie_informacao,
		b.nr_seq_colunas
	from	ctb_demo_mes b
	where	b.nr_seq_demo 		= vet01.nr_sequencia);

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
