CREATE OR REPLACE PROCEDURE ctb_copiar_grupo_centro ( cd_empresa_origem_p bigint, cd_empresa_destino_p bigint, nm_usuario_p text) AS $body$
DECLARE


	/*Variaveis de controle*/

	cd_grupo_w	bigint;
	nr_grupo_w	bigint;

	/*Cursor com dados filtrados */

	c1 CURSOR FOR
	SELECT	cd_grupo,
		cd_empresa_destino_p cd_empresa,
		ds_grupo,
		clock_timestamp() dt_atualizacao,
		nm_usuario_p nm_usuario,
		cd_mascara,
		clock_timestamp() dt_atualizacao_nrec,
		nm_usuario_p nm_usuario_nrec,
		ie_tipo
	from	ctb_grupo_centro
	where	cd_empresa	=	cd_empresa_origem_p;

	r1 c1%rowtype;


BEGIN

/*Obtem ultimo grupo cadastrado*/

select	max(cd_grupo)
into STRICT	cd_grupo_w
from	ctb_grupo_centro;

open c1;
loop
fetch c1 into r1;
EXIT WHEN NOT FOUND; /* apply on c1 */

	/* Verifica se o grupo de centro de custo ja foi copiado anteriormente pra esta empresa, usando como comparação a empresa destiuno e a cd_grupo_ref*/

	select	count(*)
	into STRICT	nr_grupo_w
	from	ctb_grupo_centro
	where	cd_grupo_ref	=	r1.cd_grupo
	and	cd_empresa	=	cd_empresa_destino_p;
	
	if (	nr_grupo_w = 0 ) then
	
		cd_grupo_w := cd_grupo_w + 1;
		
		insert into ctb_grupo_centro(
			cd_grupo,
			cd_empresa,
			ds_grupo,
			dt_atualizacao,
			nm_usuario,
			cd_mascara,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			ie_tipo,
			cd_grupo_ref)
		values (cd_grupo_w,
			r1.cd_empresa,
			r1.ds_grupo,
			r1.dt_atualizacao,
			r1.nm_usuario,
			r1.cd_mascara,
			r1.dt_atualizacao_nrec,
			r1.nm_usuario_nrec,
			r1.ie_tipo,
			r1.cd_grupo);
			
	end if;

end loop;
close c1;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
