CREATE OR REPLACE PROCEDURE ctb_copiar_historico_padrao ( cd_empresa_origem_p bigint, cd_empresa_destino_p bigint, nm_usuario_p text) AS $body$
DECLARE


	/*Variaveis de controle*/

	cd_historico_w	bigint;
	nr_historico_w	bigint;

	/*Cursor com dados filtrados */

	c1 CURSOR FOR
	SELECT	cd_historico,
		ds_historico,
		clock_timestamp() dt_atualizacao,
		nm_usuario_p nm_usuario,
		cd_empresa_destino_p cd_empresa,
		clock_timestamp() dt_atualizacao_nrec,
		nm_usuario_p nm_usuario_nrec,
		cd_sistema_contabil,
		ie_situacao
	from	historico_padrao
	where	cd_empresa	=	cd_empresa_origem_p;

	r1 c1%rowtype;
	


BEGIN

/*Obtem ultimo historico cadastrado*/

select	max(cd_historico)
into STRICT	cd_historico_w
from	historico_padrao;

open c1;
loop
fetch c1 into r1;
EXIT WHEN NOT FOUND; /* apply on c1 */

	/* Verifica se o historico_padrao ja foi copiado anteriormente pra esta empresa, usando como comparação a empresa destiuno e a cd_historico_ref*/

	select	count(*)
	into STRICT	nr_historico_w
	from	historico_padrao
	where	cd_historico_ref	=	r1.cd_historico
	and	cd_empresa		=	cd_empresa_destino_p;
	
	if (	nr_historico_w = 0 ) then
	
		cd_historico_w := cd_historico_w + 1;
		
		insert into historico_padrao(
			cd_historico,
			ds_historico,
			dt_atualizacao,
			nm_usuario,
			cd_empresa,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			cd_sistema_contabil,
			ie_situacao,
			cd_historico_ref)
		values (cd_historico_w,
			r1.ds_historico,
			r1.dt_atualizacao,
			r1.nm_usuario,
			r1.cd_empresa,
			r1.dt_atualizacao_nrec,
			r1.nm_usuario_nrec,
			r1.cd_sistema_contabil,
			r1.ie_situacao,
			r1.cd_historico);
	end if;

end loop;
close c1;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
