CREATE OR REPLACE PROCEDURE ctb_copiar_lib_orc_usuario ( cd_empresa_p bigint, nm_usuario_origem_p text, nm_usuario_dest_p text, ie_lib_orc_p text, ie_lib_orc_conta_p text, nm_usuario_p text) AS $body$
BEGIN

if (ie_lib_orc_p = 'S') then

	insert into ctb_lib_orcamento(
		nr_sequencia,
		cd_empresa,
		nm_usuario_lib,
		dt_atualizacao,
		nm_usuario,
		cd_classif_centro,
		ie_atualizacao,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		cd_estabelecimento,
		cd_centro_custo)
	SELECT	nextval('ctb_lib_orcamento_seq'),
		a.cd_empresa,
		nm_usuario_dest_p,
		clock_timestamp(),
		nm_usuario_p,
		a.cd_classif_centro,
		a.ie_atualizacao,
		clock_timestamp(),
		nm_usuario_p,
		a.cd_estabelecimento,
		a.cd_centro_custo
	from	ctb_lib_orcamento a
	where	a.nm_usuario_lib	= nm_usuario_origem_p;
	
end if;

if (ie_lib_orc_conta_p = 'S') then
	begin
	
	insert into ctb_lib_orc_conta(
		nr_sequencia,
		cd_empresa,
		nm_usuario_lib,
		dt_atualizacao,
		nm_usuario,
		cd_conta_contabil,
		ie_atualizacao,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		cd_estabelecimento,
		ie_conta_inferior,
		ie_centro_custo,
		ie_permissao_visual)
	SELECT	nextval('ctb_lib_orc_conta_seq'),
		a.cd_empresa,
		nm_usuario_dest_p,
		clock_timestamp(),
		nm_usuario_p,
		a.cd_conta_contabil,
		a.ie_atualizacao,
		clock_timestamp(),
		nm_usuario_p,
		a.cd_estabelecimento,
		a.ie_conta_inferior,
		a.ie_centro_custo,
		a.ie_permissao_visual
	from	ctb_lib_orc_conta a
	where	a.nm_usuario_lib	= nm_usuario_origem_p;
	
	end;
end if;

commit;

end;
$body$
