CREATE OR REPLACE PROCEDURE ctb_copiar_metrica ( nr_seq_metrica_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_metrica_w		bigint;

BEGIN

select	nextval('ctb_metrica_seq')
into STRICT	nr_seq_metrica_w
;

insert into ctb_metrica(
	nr_sequencia,
	cd_empresa,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	ds_metrica,
	ds_observacao,
	ie_calculo_metrica_real)
(SELECT	nr_seq_metrica_w,
	cd_empresa,
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	ds_metrica,
	ds_observacao,
	ie_calculo_metrica_real
from	ctb_metrica
where	nr_sequencia = nr_seq_metrica_p);

insert into ctb_orc_metrica(
	nr_sequencia,
	cd_estabelecimento,
	nr_seq_metrica,
	dt_atualizacao,
	nm_usuario,
	cd_centro_custo,
	qt_limite)
(SELECT	nextval('ctb_orc_metrica_seq'),
	cd_estabelecimento,
	nr_seq_metrica_w,
	clock_timestamp(),
	nm_usuario_p,
	cd_centro_custo,
	qt_limite
from	ctb_orc_metrica
where	nr_seq_metrica = nr_seq_metrica_p);

insert into ctb_orc_metrica_calc(
	nr_sequencia,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	nr_seq_metrica,
	ds_sql,
	dt_inicio_vigencia,
	dt_fim_vigencia)
(SELECT	nextval('ctb_orc_metrica_calc_seq'),
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	nr_seq_metrica_w,
	ds_sql,
	dt_inicio_vigencia,
	dt_fim_vigencia
from	ctb_orc_metrica_calc
where	nr_seq_metrica = nr_seq_metrica_p);

commit;

end;
