CREATE OR REPLACE PROCEDURE ctb_definir_agrup_dia_lote ( cd_empresa_p empresa.cd_empresa%type, nr_seq_mes_ref_p ctb_mes_ref.nr_sequencia%type, dt_dia_movimento_p ctb_movimento.dt_movimento%type, nm_usuario_p text) AS $body$
DECLARE


dt_inicio_dia_w				timestamp;
dt_final_dia_w				timestamp;
nr_lote_contabil_w			lote_contabil.nr_lote_contabil%type;
nr_seq_agrupamento_w			ctb_movimento.nr_seq_agrupamento%type;

C01 CURSOR FOR
SELECT 	a.nr_lote_contabil
from	lote_contabil a
where 	a.nr_seq_mes_ref	= nr_seq_mes_ref_p
and	exists (	select 	1
		from 	ctb_movimento b
		where 	b.nr_lote_contabil = a.nr_lote_contabil
		and 	b.dt_movimento between dt_inicio_dia_w and dt_final_dia_w)
order by a.nr_lote_contabil;

BEGIN

dt_inicio_dia_w		:= trunc(dt_dia_movimento_p);
dt_final_dia_w		:= fim_dia(dt_dia_movimento_p);
nr_seq_agrupamento_w	:= somente_numero(to_char(dt_dia_movimento_p, 'DDMMYYYY'));

open C01;
loop
fetch C01 into
	nr_lote_contabil_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	update	ctb_movimento
	set	nr_seq_agrupamento	= nr_seq_agrupamento_w,
		dt_atualizacao		= clock_timestamp(),
		nm_usuario		= nm_usuario_p
	where	dt_movimento between dt_inicio_dia_w and dt_final_dia_w
	and 	coalesce(nr_seq_agrupamento, 0) = 0
	and 	nr_lote_contabil = nr_lote_contabil_w;
	end;
end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
