CREATE OR REPLACE PROCEDURE ctb_definir_eliminacao ( nr_seq_mes_ref_p text, cd_estabelecimento_p text, nr_seq_movimento_p text, ie_eliminacao_lancto_p text, nr_seq_movto_eliminacao_p bigint default null) AS $body$
BEGIN

	if (ie_eliminacao_lancto_p = 'S') then
		begin
		update  ctb_movimento
		set     ie_eliminacao_lancto = ie_eliminacao_lancto_p,
				nr_seq_movto_eliminacao = nr_seq_movto_eliminacao_p
		where   nr_sequencia = nr_seq_movimento_p;

		update  ctb_movimento
		set     ie_eliminacao_lancto = ie_eliminacao_lancto_p
		where   nr_sequencia = nr_seq_movto_eliminacao_p;
		end;
	else
		begin
		update  ctb_movimento
		set     ie_eliminacao_lancto = ie_eliminacao_lancto_p
				where   nr_sequencia = (SELECT  b.nr_seq_movto_eliminacao
										from    ctb_movimento b
										where   b.nr_sequencia = nr_seq_movimento_p);
		
		update  ctb_movimento
		set     ie_eliminacao_lancto = ie_eliminacao_lancto_p,
				nr_seq_movto_eliminacao  = NULL
		where   nr_sequencia = nr_seq_movimento_p;
		end;
	end if;

end;
