CREATE OR REPLACE PROCEDURE ctb_definir_lancto_eliminacao ( nr_seq_mes_ref_p text, cd_estabelecimento_p text, ie_lancamento_p text default 'I') AS $body$
BEGIN

    if ie_lancamento_p = 'I' then

      update ctb_mes_ref
      set dt_inicio_eliminacao = clock_timestamp(), dt_fim_eliminacao  = NULL
      where nr_sequencia = nr_seq_mes_ref_p;

    else
    
      update ctb_mes_ref
      set dt_fim_eliminacao = clock_timestamp()
      where nr_sequencia = nr_seq_mes_ref_p;

    end if;

    commit;

end;
$body$
