CREATE OR REPLACE PROCEDURE ctb_definir_valor_orig_orc ( ie_sobrepor_p text, dt_ano_ref_p text, nm_usuario_p text) AS $body$
BEGIN

if (ie_sobrepor_p	= 'S') then

	update	ctb_orcamento
	set	vl_original	= vl_orcado
	where	to_char(ctb_obter_mes_ref(nr_seq_mes_ref),'yyyy')	= dt_ano_ref_p;

else

	update	ctb_orcamento
	set	vl_original	= vl_orcado
	where	to_char(ctb_obter_mes_ref(nr_seq_mes_ref),'yyyy')	= dt_ano_ref_p
	and	vl_original	= 0;

end if;

commit;

end;
$body$
