SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ctb_desatualizar_mes ( nr_seq_mes_ref_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, ds_erro_p INOUT text) AS $body$
DECLARE


dt_abertura_w			timestamp;
dt_fechamento_w		timestamp;
ds_erro_w			varchar(2000);


BEGIN

select dt_abertura,
	dt_fechamento
into STRICT 	dt_abertura_w,
	dt_fechamento_w
from	ctb_mes_ref
where	nr_sequencia	= nr_seq_mes_ref_p;

if (coalesce(dt_abertura_w::text, '') = '')  then
	/*'O mês não está aberto');*/

	CALL wheb_mensagem_pck.exibir_mensagem_abort(207401);
end if;

if (dt_fechamento_w IS NOT NULL AND dt_fechamento_w::text <> '') then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(207399);
end if;

update	ctb_saldo
set	vl_debito		= 0,
	vl_credito		= 0,
	vl_movimento	= 0,
	vl_encerramento	= 0,
	vl_enc_debito	= 0,
	vl_enc_credito	= 0,
	vl_saldo		= vl_saldo - vl_movimento,
	nm_usuario	= nm_usuario_p,
	dt_atualizacao	= clock_timestamp()
where	nr_seq_mes_ref	= nr_seq_mes_ref_p
and	coalesce(cd_estabelecimento_p, cd_estabelecimento) = cd_estabelecimento;

update	lote_contabil
set	dt_atualizacao_saldo	 = NULL,
	dt_consistencia		 = NULL,
	nm_usuario		= nm_usuario_p,
	dt_atualizacao		= clock_timestamp()
where	nr_seq_mes_ref		= nr_seq_mes_ref_p
and	coalesce(cd_estabelecimento_p, cd_estabelecimento) = cd_estabelecimento;

ds_erro_w := CTB_Acumular_Saldo(nr_seq_mes_ref_p, cd_estabelecimento_p, nm_usuario_p, ds_erro_w);

commit;

ds_erro_p	:= ds_erro_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE ctb_desatualizar_mes ( nr_seq_mes_ref_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, ds_erro_p INOUT text) FROM PUBLIC;

