CREATE OR REPLACE PROCEDURE ctb_desfazer_lote_rateio ( nr_seq_mes_ref_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


dt_atualizacao_saldo_w		timestamp;
nr_lote_contabil_w			bigint;


BEGIN

select	max(nr_lote_contabil)
into STRICT	nr_lote_contabil_w
from	ctb_regra_rateio
where	nr_seq_mes_ref	= nr_seq_mes_ref_p
and	cd_estabelecimento = cd_estabelecimento_p;

if (nr_lote_contabil_w IS NOT NULL AND nr_lote_contabil_w::text <> '') then

	select	max(dt_atualizacao_saldo)
	into STRICT	dt_atualizacao_saldo_w
	from	lote_contabil
	where	nr_lote_contabil	= nr_lote_contabil_w;

	if (dt_atualizacao_saldo_w IS NOT NULL AND dt_atualizacao_saldo_w::text <> '') then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(217014);
	else
		delete	FROM ctb_movimento
		where	nr_lote_contabil	= nr_lote_contabil_w;

		update	ctb_regra_rateio
		set	nr_lote_contabil	 = NULL
		where	nr_lote_contabil	= nr_lote_contabil_w;

		delete	FROM lote_contabil
		where	nr_lote_contabil	= nr_lote_contabil_w;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
