CREATE OR REPLACE PROCEDURE ctb_desvincular_movimento ( nr_lote_contabil_p bigint) AS $body$
DECLARE


cd_tipo_lote_contabil_w		lote_contabil.cd_tipo_lote_contabil%type;


BEGIN

select	max(cd_tipo_lote_contabil)
into STRICT	cd_tipo_lote_contabil_w
from	lote_contabil
where	nr_lote_contabil	= nr_lote_contabil_p;

if (ctb_obter_se_concilia_movto(cd_tipo_lote_contabil_w) = 'S') then
	update	ctb_documento	a
	set	a.nr_lote_contabil	= 0,
		ie_status		= 'N'
	where	a.nr_lote_contabil	= nr_lote_contabil_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
