SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ctb_duplicar_historico_padrao ( cd_historico_p bigint, nm_usuario_p text) AS $body$
DECLARE



cd_historico_w		bigint;


BEGIN

select	coalesce(max(cd_historico),0) + 1
into STRICT	cd_historico_w
from	historico_padrao;

insert into historico_padrao(cd_historico,
	ds_historico,
	dt_atualizacao,
	nm_usuario,
	cd_empresa,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	cd_sistema_contabil,
	ie_situacao)
(SELECT	cd_historico_w,
	substr(wheb_mensagem_pck.get_texto(298835) || ds_historico,1,255),
	clock_timestamp(),
	nm_usuario_p,
	cd_empresa,
	clock_timestamp(),
	nm_usuario_p,
	cd_sistema_contabil,
	ie_situacao
from	historico_padrao
where	cd_historico	= cd_historico_p);

insert into historico_padrao_atributo(nr_sequencia,
	cd_historico,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	cd_tipo_lote_contabil,
	nr_seq_atributo,
	nr_seq_apres,
	ds_caracter_pre,
	ds_caracter_pos,
	ie_funcao,
	ds_mascara)
(SELECT	nextval('historico_padrao_atributo_seq'),
	cd_historico_w,
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	cd_tipo_lote_contabil,
	nr_seq_atributo,
	nr_seq_apres,
	ds_caracter_pre,
	ds_caracter_pos,
	ie_funcao,
	ds_mascara
from	historico_padrao_atributo
where	cd_historico	= cd_historico_p);


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
