CREATE OR REPLACE PROCEDURE ctb_estornar_lib_orc ( cd_empresa_p bigint, cd_estabelecimento_p bigint, nr_seq_mes_ref_p bigint, cd_centro_custo_p bigint, cd_conta_contabil_p text, nm_usuario_p text) AS $body$
DECLARE


cd_estabelecimento_w		bigint;
ie_estornar_lib_orc_mes_w	varchar(1);
nr_sequencia_w			bigint;
qt_sem_liberacao_w		bigint;
/* Orçamentos da conta no mes no estabelecimento e no centro de custo */

c01 CURSOR FOR
SELECT	a.nr_sequencia,
	a.cd_estabelecimento
from	estabelecimento b,
	ctb_orcamento a
where	a.cd_estabelecimento	= b.cd_estabelecimento
and	a.nr_seq_mes_ref		= nr_seq_mes_ref_p
and	a.cd_centro_custo		= cd_centro_custo_p
and	a.cd_conta_contabil	= cd_conta_contabil_p
and	b.cd_empresa		= cd_empresa_p
and	b.cd_estabelecimento	= cd_estabelecimento_p
and (ie_estornar_lib_orc_mes_w = 'S' or a.nm_usuario_lib = nm_usuario_p)
and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '');


BEGIN

select	count(*)
into STRICT	qt_sem_liberacao_w
from	ctb_orcamento
where	nr_seq_mes_ref	= nr_seq_mes_ref_p
and	coalesce(dt_liberacao::text, '') = '';

ie_estornar_lib_orc_mes_w	:= coalesce(obter_valor_param_usuario(925, 40, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p), 'N');

if (qt_sem_liberacao_w = 0) and (ie_estornar_lib_orc_mes_w = 'N') then
	/*O orçamento do mês já foi liberado, não é permitido o estorno da liberação!#@#@');*/

	CALL wheb_mensagem_pck.exibir_mensagem_abort(266451);
end if;

open C01;
loop
fetch C01 into
	nr_sequencia_w,
	cd_estabelecimento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	CALL ctb_liberar_orcamento(cd_empresa_p, cd_estabelecimento_w, nr_sequencia_w, 'E', nm_usuario_p);

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
