SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ctb_excluir_movto_lote ( nr_sequencia_p bigint, nr_lote_contabil_p bigint, nm_usuario_p text) AS $body$
DECLARE


integrity_constraint_violation exception;

ds_erro_w varchar(4000);
ds_log_W			varchar(255);
qtd_delete_w		integer;


BEGIN

if (coalesce(nr_sequencia_p,0) <> 0) then
	delete	from ctb_movimento
	where	nr_lote_contabil 	= nr_lote_contabil_p
	and	nr_sequencia		= nr_sequencia_p;

	ds_log_w	:= WHEB_MENSAGEM_PCK.get_texto(336004) || ' ' || nr_sequencia_p;
else
	loop
	update CTB_MOVIMENTO
	set nr_seq_movto_corresp  = NULL,
	    nr_seq_movto_partida  = NULL 
	where nr_lote_contabil = nr_lote_contabil_p
	and ((nr_seq_movto_corresp IS NOT NULL AND nr_seq_movto_corresp::text <> '') or (nr_seq_movto_partida IS NOT NULL AND nr_seq_movto_partida::text <> ''))  LIMIT 1000;
	
	GET DIAGNOSTICS qtd_delete_w = ROW_COUNT;
	commit;
	
	if (qtd_delete_w = 0) then
		exit;
	end if;
	
	end loop;

	loop
	delete	from ctb_movimento
	where	nr_lote_contabil = nr_lote_contabil_p  LIMIT 1000;
	
	GET DIAGNOSTICS qtd_delete_w = ROW_COUNT;
	commit;
	
	if (qtd_delete_w = 0) then
		exit;
	end if;
	
	end loop;
	
	delete	from ctb_movimento
	where	nr_lote_contabil = nr_lote_contabil_p;
end if;

CALL ctb_gravar_log_lote(nr_lote_contabil_p,8,ds_log_w,nm_usuario_p);

commit;

exception when integrity_constraint_violation then
    ds_erro_w := substr(sqlerrm(SQLSTATE),1,4000);
    rollback;
    /*Nao foi possivel excluir os movimentos por existirem dependencias
    Verifique se foram selecionadas todas as partidas e contrapartidas relacionadas
    Detalhes DSERRO*/
    CALL wheb_mensagem_pck.exibir_mensagem_abort(1077102);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
