CREATE OR REPLACE PROCEDURE ctb_excluir_plano_conta_ifrs ( cd_empresa_p ctb_plano_conta_ifrs.cd_empresa%type, ie_excluir_vinculo_p text) AS $body$
BEGIN

if (coalesce(ie_excluir_vinculo_p, 'N') = 'S') then
	begin

	delete	FROM conta_contabil_ifrs a
	where exists (	SELECT	1
					from	ctb_plano_conta_ifrs x
					where   x.nr_sequencia	    = a.nr_seq_conta_ifrs
					and	    x.cd_empresa		= cd_empresa_p)
	and	exists (	select	1
			from	conta_contabil y
			where	y.cd_conta_contabil	= a.cd_conta_contabil
			and	y.cd_empresa		= cd_empresa_p)
    and not exists (select	1
			from	ctb_saldo_ifrs z
			where	z.nr_seq_conta_ifrs	= a.nr_sequencia);

    delete	FROM ctb_plano_conta_ifrs a
        where cd_empresa = cd_empresa_p
        and not exists (SELECT	1
                    from	ctb_saldo_ifrs z
                    where	z.nr_seq_conta_ifrs	= a.nr_sequencia);
    end;
end if;

delete	FROM ctb_plano_conta_ifrs a
where cd_empresa = cd_empresa_p
and not exists (SELECT	1
                    from	ctb_saldo_ifrs y
                    where	y.nr_seq_conta_ifrs	= a.nr_sequencia)
and not exists (select	1
			from	conta_contabil_ifrs z
			where	z.nr_seq_conta_ifrs	= a.nr_sequencia);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
