SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ctb_fechar_conciliacao ( nr_seq_concil_p bigint, nm_usuario_p text, ie_operacao_p text) AS $body$
BEGIN

if (ie_operacao_p = 'F') then

	update 	ctb_movimento a
	set 	a.ie_status_concil 	 = NULL,
		a.nr_seq_reg_concil 	 = NULL
	where 	a.nr_seq_reg_concil	= nr_seq_concil_p
	and		a.ie_status_concil <> 'C';

end if;

update	ctb_registro_concil
set	dt_fechamento		= CASE WHEN ie_operacao_p='F' THEN  clock_timestamp()  ELSE null END ,
	nm_usuario_fechamento	= CASE WHEN ie_operacao_p='F' THEN  nm_usuario_p  ELSE null END
where	nr_sequencia		= nr_seq_concil_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
