CREATE OR REPLACE PROCEDURE ctb_gerar_arquivo_exp_movto ( nr_lote_contabil_p bigint, nm_arquivo_p INOUT text) AS $body$
DECLARE


dt_inicial_w			varchar(10);
dt_final_w			varchar(10);
dt_final_inv_w			varchar(10);
cd_tipo_lote_contabil_w		bigint;
nm_arquivo_w			varchar(255);
dt_referencia_w			varchar(10);


BEGIN

select	cd_tipo_lote_contabil,
	to_char(trunc(dt_referencia,'dd'),'ddmmyyyy'),
	to_char(last_day(trunc(dt_referencia,'dd')),'yyyymmdd'),
	to_char(trunc(dt_referencia,'dd'),'yyyymmdd')
into STRICT	cd_tipo_lote_contabil_w,
	dt_inicial_w,
	dt_final_inv_w,
	dt_referencia_w
from	lote_contabil
where	nr_lote_contabil = nr_lote_contabil_p;

select	to_char(trunc(max(dt_movimento),'dd'),'ddmmyyyy')
into STRICT	dt_final_w
from	ctb_movimento
where	nr_lote_contabil = nr_lote_contabil_p;

select	nm_arquivo_exp_movimento
into STRICT	nm_arquivo_w
from	tipo_lote_contabil
where	cd_tipo_lote_contabil = cd_tipo_lote_contabil_w;

select	substr(
	replace_macro(
	replace_macro(
	replace_macro(
	replace_macro(
	replace_macro(
	replace_macro(nm_arquivo_w,
		'@dt_inicial',dt_inicial_w),
		'@dt_final_inv',dt_final_inv_w),
		'@nr_lote_contabil',nr_lote_contabil_p),
		'@dt_final',dt_final_w),
		'@data_atual',to_char(clock_timestamp(),('DDMMYYYYHH24MISS'))),
		'@dt_ref',dt_referencia_w),1,255)
into STRICT	nm_arquivo_w
;

nm_arquivo_p	:= nm_arquivo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
