CREATE OR REPLACE PROCEDURE ctb_gerar_classif_ecd ( cd_estabelecimento_p bigint, cd_conta_contabil_p text, ie_tipo_custo_p text, cd_classif_ecd_p text, nm_usuario_p text) AS $body$
DECLARE


cd_centro_custo_w			varchar(8);
qt_registro_w			bigint;

C01 CURSOR FOR
SELECT	cd_centro_custo
from	centro_custo a
where	cd_estabelecimento	= cd_estabelecimento_p
and	ie_tipo		= 'A'
and	ie_tipo_custo	= ie_tipo_custo_p
and	ie_situacao	= 'A'
and	not exists (	select	1
			from	conta_contabil_classif_ecd y
			where	y.cd_centro_custo	= a.cd_centro_custo
			and	y.cd_conta_contabil	= cd_conta_contabil_p
			and	y.cd_classif_ecd	= cd_classif_ecd_p);


BEGIN

open C01;
loop
fetch C01 into
	cd_centro_custo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin


	insert into conta_contabil_classif_ecd(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		cd_conta_contabil,
		cd_classif_ecd,
		cd_centro_custo,
		ie_situacao)
	values (	nextval('conta_contabil_classif_ecd_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		cd_conta_Contabil_p,
		cd_classif_ecd_p,
		cd_centro_custo_w,
		'A');

	end;

end loop;
close C01;

commit;

end;
$body$
