CREATE OR REPLACE PROCEDURE ctb_gerar_conta_contabil_estab ( cd_conta_contabil_p text, cd_estab_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_registro_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_registro_w
from	conta_contabil_estab
where	cd_conta_contabil 	= cd_conta_contabil_p
and	cd_estabelecimento	= cd_estab_p;

if (qt_registro_w = 0) then
	begin

	insert	into conta_contabil_estab(
		nr_sequencia,
		cd_centro_custo,
		cd_conta_contabil,
		cd_estabelecimento,
		dt_atualizacao,
		dt_atualizacao_nrec,
		nm_usuario,
		nm_usuario_nrec,
		nr_seq_crit_rateio)
	values (	nextval('conta_contabil_estab_seq'),
		null,
		cd_conta_contabil_p,
		cd_estab_p,
		clock_timestamp(),
		clock_timestamp(),
		nm_usuario_p,
		nm_usuario_p,
		null);
	end;
end if;

commit;

end;
$body$
