CREATE OR REPLACE PROCEDURE ctb_gerar_interf_9900_ecd ( nr_seq_controle_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, dt_inicio_p timestamp, dt_fim_p timestamp, cd_empresa_p bigint, qt_linha_p INOUT bigint, nr_sequencia_p INOUT bigint) AS $body$
DECLARE



qt_linhas_w			bigint;
cd_registro_w			varchar(5);
ds_arquivo_w			varchar(4000);
ds_compl_arquivo_w		varchar(4000);
ds_linha_w			varchar(8000);
nr_linha_w			bigint := qt_linha_p;
nr_seq_registro_w			bigint := nr_sequencia_p;
sep_w				varchar(1) := '|';
tp_registro_w			varchar(15) := '9900';
nr_seq_regra_sped_w		bigint;

c01 CURSOR FOR
SELECT	y.cd_registro,
	y.qt_linhas,
	b.nr_seq_apresent
from (
select	cd_registro,
	count(*) qt_linhas
from  	ctb_sped_registro
where	nr_seq_controle_sped	= nr_seq_controle_p
group by cd_registro

union

select cd_registro,
	1 qt_linhas
from 	ctb_regra_sped_registro
where	cd_registro in ('9900','9990', '9999')
and 	nr_seq_regra_sped 	= nr_seq_regra_sped_w) y,
	valor_dominio b
where	y.cd_registro	= b.vl_dominio
and	b.cd_dominio	= 3508
order by 3;

vet01	C01%RowType;


BEGIN

select	max(nr_seq_regra_sped)
into STRICT	nr_seq_regra_sped_w
from	ctb_sped_controle
where	nr_sequencia	= nr_seq_controle_p;


open C01;
loop
fetch C01 into
	vet01;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	qt_linhas_w	:= vet01.qt_linhas;

	/*Contar os registros do pr√≥prio bloco */

	if (vet01.cd_registro	= tp_registro_w) then

		select	count(cd_registro)
		into STRICT	qt_linhas_w
		from	ctb_sped_registro
		where	nr_seq_controle_sped	= nr_seq_controle_p
		and	cd_registro		= tp_registro_w;

		qt_linhas_w	:= coalesce(qt_linhas_w,0)	+ 3;

	end if;


	ds_linha_w	:= substr(		sep_w || tp_registro_w 		||
					sep_w || vet01.cd_registro		||
					sep_w || qt_linhas_w 		|| sep_w,1,8000);

	ds_arquivo_w		:= substr(ds_linha_w,1,4000);
	ds_compl_arquivo_w	:= substr(ds_linha_w,4001,4000);
	nr_seq_registro_w	:= nr_seq_registro_w + 1;
	nr_linha_w		:= nr_linha_w + 1;
	insert into ctb_sped_registro(
			nr_sequencia,
			ds_arquivo,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_seq_controle_sped,
			ds_arquivo_compl,
			cd_registro,
			nr_linha)
		values (
			nr_seq_registro_w,
			ds_arquivo_w,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_controle_p,
			ds_compl_arquivo_w,
			tp_registro_w,
			nr_linha_w);

	end;
end loop;
close C01;

commit;
qt_linha_p	:= nr_linha_w;
nr_sequencia_p	:= nr_seq_registro_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
