CREATE OR REPLACE PROCEDURE ctb_gerar_interf_i075_ecd ( nr_seq_controle_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, dt_inicio_p timestamp, dt_fim_p timestamp, cd_empresa_p bigint, qt_linha_p INOUT bigint, nr_sequencia_p INOUT bigint) AS $body$
DECLARE



cd_historico_w			bigint;
ds_historico_w			varchar(255);
ds_arquivo_w			varchar(4000);
ds_compl_arquivo_w		varchar(4000);
ds_linha_w			varchar(8000);
nr_linha_w			bigint := qt_linha_p;
nr_seq_registro_w			bigint := nr_sequencia_p;
sep_w				varchar(1) := '|';
tp_registro_w			varchar(15) := 'I075';
ie_consolida_empresa_w	ctb_regra_sped.ie_consolida_empresa%type;

c01 CURSOR FOR
SELECT	distinct	h.cd_historico,
		replace(replace(h.ds_historico,chr(13),null),chr(10),null) ds_historico
from	historico_padrao h,
		ctb_movimento a,
		lote_contabil b
where	a.cd_historico		= h.cd_historico
and a.nr_lote_contabil	= b.nr_lote_contabil
and (obter_empresa_estab(b.cd_estabelecimento) = h.cd_empresa or h.nr_seq_grupo_emp = holding_pck.get_acesso_grupo_emp(
		923,
		wheb_usuario_pck.get_cd_perfil,
		nm_usuario_p,
		NULL,
		'GEM',
		obter_empresa_estab(cd_estabelecimento_p)))
and (ie_consolida_empresa_w = 'S' or b.cd_estabelecimento = cd_estabelecimento_p)
and	a.dt_movimento between dt_inicio_p and dt_fim_p
and	(trim(both h.ds_historico) IS NOT NULL AND (trim(both h.ds_historico))::text <> '')
order by h.cd_historico;


BEGIN

select	max(a.ie_consolida_empresa)
into STRICT	ie_consolida_empresa_w
from	ctb_regra_sped a,
	ctb_sped_controle b
where	b.nr_seq_regra_sped 	= a.nr_sequencia
and	b.nr_sequencia		= nr_seq_controle_p;

open C01;
loop
fetch C01 into	
	cd_historico_w,
	ds_historico_w;	
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin	
	ds_linha_w	:= substr(		sep_w || tp_registro_w 	||
					sep_w || cd_historico_w 	|| 
					sep_w || ds_historico_w 	|| sep_w,1,8000);
	
	ds_arquivo_w		:= substr(ds_linha_w,1,4000);
	ds_compl_arquivo_w	:= substr(ds_linha_w,4001,4000);
	nr_seq_registro_w		:= nr_seq_registro_w + 1;
	nr_linha_w		:= nr_linha_w + 1;	

	insert into ctb_sped_registro(
		nr_sequencia,
		ds_arquivo,                     
		dt_atualizacao,                 
		nm_usuario,                     
		dt_atualizacao_nrec,            
		nm_usuario_nrec,                
		nr_seq_controle_sped,           
		ds_arquivo_compl,               
		cd_registro,
		nr_linha)
	values (	nr_seq_registro_w,
		ds_arquivo_w,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_controle_p,
		ds_compl_arquivo_w,
		tp_registro_w,
		nr_linha_w);

	end;
end loop;
close C01;

commit;
qt_linha_p	:= nr_linha_w;
nr_sequencia_p	:= nr_seq_registro_w;	
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
