SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ctb_gerar_interf_i150_fcont ( nr_seq_controle_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, dt_inicio_p timestamp, dt_fim_p timestamp, cd_empresa_p bigint, qt_linha_p INOUT bigint, nr_sequencia_p INOUT bigint) AS $body$
DECLARE


dt_inicio_w			timestamp;
dt_fim_w				timestamp;
cd_conta_contabil_w		varchar(40);
/* Valores formatados */

vl_saldo_inicial_ww			varchar(80);
vl_debito_ww			varchar(80);
vl_credito_ww			varchar(80);
vl_saldo_final_ww			varchar(80);
nr_linha_w			bigint	:= qt_linha_p;
nr_seq_registro_w			bigint	:= nr_sequencia_p;
ds_arquivo_w			varchar(4000);
ds_compl_arquivo_w		varchar(4000);
ds_linha_w			varchar(8000);
sep_w				varchar(1)	:= '|';
tp_registro_w			varchar(15);
ie_apres_conta_ctb_w		varchar(15);
ie_periodo_w			varchar(15);
ie_consolida_empresa_w		ctb_regra_sped.ie_consolida_empresa%type;

C01 CURSOR FOR
	SELECT	trunc(dt_inicio_p,'mm') dt_inicio,
		fim_mes(dt_fim_p) dt_fim
	
	where	ie_periodo_w	= 'A'
	
union

	SELECT	trunc(add_months(b.dt_referencia,-2),'mm') dt_inicio,
		fim_mes(b.dt_referencia) dt_fim
	from	ctb_mes_ref	b
	where	b.dt_referencia	between dt_inicio_w and dt_fim_w
	and	b.cd_empresa	= cd_empresa_p
	and	ie_periodo_w	= 'T'
	and	substr(to_char(b.dt_referencia,'mm'),1,2) in ('03','06','09','12')
	order by
		1;

C02 CURSOR FOR
	SELECT	a.cd_conta_contabil,
		substr(ctb_obter_classif_conta(a.cd_conta_contabil, a.cd_classificacao, dt_inicio_p),1,40) cd_classificacao,
		substr(ctb_obter_situacao_saldo(a.cd_conta_contabil, coalesce(sum(CASE WHEN a.dt_inicial=dt_inicio_w THEN  a.vl_saldo_inicial  ELSE 0 END ),0)),1,1) ie_debito_credito_inicial,
		coalesce(sum(CASE WHEN a.dt_inicial=dt_inicio_w THEN  a.vl_saldo_inicial  ELSE 0 END ),0) vl_saldo_inicial,
		coalesce(sum(a.vl_debito),0) vl_debito,
		coalesce(sum(a.vl_credito),0) vl_credito,
		coalesce(sum(CASE WHEN a.dt_inicial=trunc(dt_fim_w,'mm') THEN  a.vl_saldo_final  ELSE 0 END ),0) vl_saldo_final,
		substr(ctb_obter_situacao_saldo(a.cd_conta_contabil, coalesce(sum(CASE WHEN a.dt_inicial=trunc(dt_fim_w,'mm') THEN  a.vl_saldo_final  ELSE 0 END ),0)),1,1) ie_debito_credito_final
	from	ecd_Plano_conta_v	b,
		ecd_saldo_periodico_v	a
	where	a.cd_estabelecimento 	= cd_estabelecimento_p
	and	a.cd_empresa 		= cd_empresa_p
	and	a.tp_registro		= 2
	and	ie_consolida_empresa_w	= 'N'
	and	a.cd_conta_contabil	= b.cd_conta_contabil
	and	b.tp_registro		= 1
	and	b.cd_grupo_ecd in (1,2,3)
	and	a.dt_inicial between dt_inicio_w and dt_fim_w
	group by
		a.cd_conta_contabil,
		substr(ctb_obter_classif_conta(a.cd_conta_contabil, a.cd_classificacao, dt_inicio_p),1,40)
	
union

	/* Desconsidera o estabelecimento IE_CONSOLIDA_EMPRESA_W */

	SELECT	a.cd_conta_contabil,
		substr(ctb_obter_classif_conta(a.cd_conta_contabil, a.cd_classificacao, dt_inicio_p),1,40) cd_classificacao,
		substr(ctb_obter_situacao_saldo(a.cd_conta_contabil, coalesce(sum(CASE WHEN a.dt_inicial=dt_inicio_w THEN  a.vl_saldo_inicial  ELSE 0 END ),0)),1,1) ie_debito_credito_inicial,
		coalesce(sum(CASE WHEN a.dt_inicial=dt_inicio_w THEN  a.vl_saldo_inicial  ELSE 0 END ),0) vl_saldo_inicial,
		coalesce(sum(a.vl_debito),0) vl_debito,
		coalesce(sum(a.vl_credito),0) vl_credito,
		coalesce(sum(CASE WHEN a.dt_inicial=trunc(dt_fim_w,'mm') THEN  a.vl_saldo_final  ELSE 0 END ),0) vl_saldo_final,
		substr(ctb_obter_situacao_saldo(a.cd_conta_contabil, coalesce(sum(CASE WHEN a.dt_inicial=trunc(dt_fim_w,'mm') THEN  a.vl_saldo_final  ELSE 0 END ),0)),1,1) ie_debito_credito_final
	from	ecd_Plano_conta_v	b,
		ecd_saldo_periodico_v	a,
		estabelecimento		c
	where	a.cd_empresa 		= cd_empresa_p
	and	a.tp_registro		= 2
	and	a.cd_estabelecimento 	= c.cd_estabelecimento
	and	coalesce(ie_gerar_sped,'S')	=	'S'
	and	ie_consolida_empresa_w	= 'S'
	and	a.cd_conta_contabil	= b.cd_conta_contabil
	and	b.tp_registro		= 1
	and	b.cd_grupo_ecd in (1,2,3)
	and	a.dt_inicial between dt_inicio_w and dt_fim_w
	group by
		a.cd_conta_contabil,
		substr(ctb_obter_classif_conta(a.cd_conta_contabil, a.cd_classificacao, dt_inicio_p),1,40)
	order by
		1,
		3;

vet02	C02%RowType;


BEGIN
dt_inicio_w	:= dt_inicio_p;
dt_fim_w	:= dt_fim_p;

select	coalesce(max(a.ie_apres_conta_ctb),'CD'),
	coalesce(max(a.ie_periodo),'A'),
	coalesce(max(a.ie_consolida_empresa), 'N')
into STRICT	ie_apres_conta_ctb_w,
	ie_periodo_w,
	ie_consolida_empresa_w
from	ctb_regra_sped		a,
	ctb_sped_controle	b
where	b.nr_seq_regra_sped 	= a.nr_sequencia
and	b.nr_sequencia		= nr_seq_controle_p;

open C01;
loop
fetch C01 into
	dt_inicio_w,
	dt_fim_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	tp_registro_w	:= 'I150';
	ds_linha_w	:= substr(	sep_w || tp_registro_w 			||
					sep_w || to_char(dt_inicio_w,'ddmmyyyy') 	||
					sep_w || to_char(dt_fim_w,'ddmmyyyy') 		|| sep_w,1,8000);

	ds_arquivo_w		:= substr(ds_linha_w,1,4000);
	ds_compl_arquivo_w	:= substr(ds_linha_w,4001,4000);
	nr_seq_registro_w	:= nr_seq_registro_w + 1;
	nr_linha_w		:= nr_linha_w + 1;

	insert into ctb_sped_registro(nr_sequencia,
		ds_arquivo,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_controle_sped,
		ds_arquivo_compl,
		cd_registro,
		nr_linha)
	values (nr_seq_registro_w,
		ds_arquivo_w,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_controle_p,
		ds_compl_arquivo_w,
		tp_registro_w,
		nr_linha_w);

	tp_registro_w	:= 'I155';

	open C02;
	loop
	fetch C02 into
		vet02;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin
		cd_conta_contabil_w	:= vet02.cd_conta_contabil;

		if (ie_apres_conta_ctb_w = 'CL') then
			cd_conta_contabil_w	:= vet02.cd_classificacao;
		elsif (ie_apres_conta_ctb_w = 'CP') then
			cd_conta_contabil_w	:= substr(replace(vet02.cd_classificacao,'.',''),1,40);
		end if;

		if 	((vet02.vl_saldo_inicial <> 0) or (vet02.vl_debito <> 0)  or (vet02.vl_credito <> 0)  or (vet02.vl_saldo_final <> 0))   then
			begin
			vl_saldo_inicial_ww	:= replace(replace(campo_mascara_virgula(vet02.vl_saldo_inicial),'.',''),'-','');
			vl_debito_ww		:= replace(replace(campo_mascara_virgula(vet02.vl_debito),'.',''),'-','');
			vl_credito_ww		:= replace(replace(campo_mascara_virgula(vet02.vl_credito),'.',''),'-','');
			vl_saldo_final_ww	:= replace(replace(campo_mascara_virgula(vet02.vl_saldo_final),'.',''),'-','');

			ds_linha_w	:= substr(	sep_w || tp_registro_w 		||
							sep_w || cd_conta_contabil_w 	||
							sep_w || ''	||
							sep_w || vl_saldo_inicial_ww		||
							sep_w || vet02.ie_debito_credito_inicial	||
							sep_w || vl_debito_ww		||
							sep_w || vl_credito_ww		||
							sep_w || vl_saldo_final_ww		||
							sep_w || vet02.ie_debito_credito_final 	|| sep_w, 1,8000);

			ds_arquivo_w		:= substr(ds_linha_w,1,4000);
			ds_compl_arquivo_w	:= substr(ds_linha_w,4001,4000);
			nr_seq_registro_w	:= nr_seq_registro_w + 1;
			nr_linha_w		:= nr_linha_w + 1;

			insert into ctb_sped_registro(nr_sequencia,
				ds_arquivo,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				nr_seq_controle_sped,
				ds_arquivo_compl,
				cd_registro,
				nr_linha)
			values (nr_seq_registro_w,
				ds_arquivo_w,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				nr_seq_controle_p,
				ds_compl_arquivo_w,
				tp_registro_w,
				nr_linha_w);
			end;
		end if;
		end;
	end loop;
	close C02;
	end;
end loop;
close C01;

commit;

qt_linha_p	:= nr_linha_w;
nr_sequencia_p	:= nr_seq_registro_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE ctb_gerar_interf_i150_fcont ( nr_seq_controle_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, dt_inicio_p timestamp, dt_fim_p timestamp, cd_empresa_p bigint, qt_linha_p INOUT bigint, nr_sequencia_p INOUT bigint) FROM PUBLIC;

